import React from "react";
import { styled } from "@mui/material/styles";
import Typography from "@mui/material/Typography";
import { Paper, Box } from "@mui/material";

const Img = styled("img")({
	margin: "auto",
	display: "block",
	maxWidth: "100%",
	maxHeight: "100%",
});

const ListingCard = ({ listing, isActive, clickFunction }) => {
	const [savedBid, setSavedBid] = React.useState(0);
  	const [newBidValue, setNewBidValue] = React.useState(0);
	return (
	  <Paper
		elevation={3}
		sx={{
		  mt: 1.5,
		  mx: 2,
		  border: 2,
		  borderColor: isActive ? "primary.main" : "transparent",
		  borderRadius: "30px",
		  minWidth: { xs: "100%", sm: "100%", md: "400px", lg: "1000px" },
		  maxWidth: "1410px",
		  height: "100px",
		}}
	  >
		<Box
		  sx={{
			justifyContent: "flex-start",
			width: "100%",
			borderRadius: "30px",
			px: "14px",
			py: "4px",
			"&.Mui-active": {
			  border: "4px solid #482FD7",
			},
		  }}
		>
		  <Box
			  flex={1}
			  sx={{
				display: "flex",
				flexDirection: "horizontal",
				justifyContent: "space-between",
				height: "100%", 
				alignItems: "center"
			  }}
			>
			<Box
			  flex={1}
			  sx={{
				display: "flex",
				flexDirection: "horizontal",
				height: "100%", 
				alignItems: "center",
				maxWidth: 988
			  }}
			>
			<Box
    			maxWidth={88}
				height={88}
   				sx={{
      				display: "flex",
      				justifyContent: "flex-start",
      				alignItems: "center",
    			}}
  			>
    			<Img
      				alt={listing.company_name}
      				src={listing.image}
      				sx={{ maxWidth: 88, maxHeight: 88, width: "auto", height: "auto", borderRadius: "5px" }}
    			/>
  			</Box>
				<Box
			  	flex={1}
			  	sx={{
					display: "flex",
					flexDirection: "column",
					justifyContent: "space-between",
					height: "100%", 
					maxWidth: 898,
					paddingLeft: 2
			  	}}
			  >
			  <Typography
				textAlign="left"
				variant="h6"
				fontWeight="bold"
				sx={{
				  overflow: "hidden",
				  textOverflow: "ellipsis",
				  whiteSpace: "nowrap",
				}}
				component="div"
			  >
				{listing.company_name}
			  </Typography>
			  <Typography
				variant="subtitle2"
				textAlign="left"
				sx={{
				  display: "-webkit-box",
				  overflow: "hidden",
				  textOverflow: "ellipsis",
				  WebkitLineClamp: 2, 
				  WebkitBoxOrient: "vertical",
				}}
			  >
				{listing.description}
			  </Typography>
			  </Box>
			  </Box>
			  <Box
			  	sx={{
					paddingRight: 2,
					paddingLeft: 2
				}}>
			  	<Typography variant="body2">
              		Saved Bid: {savedBid}
              	</Typography>
			  </Box>
			  <Box>
			  	<TextField
              		type="number"
              		label="New Bid"
              		variant="outlined"
              		value={newBidValue}
              		onChange={(event) => setNewBidValue(event.target.value)}
             	 />
			  </Box>
			</Box>
		</Box>
	  </Paper>
	);
  };

export default ListingCard;
