fileInput.addEventListener("change", function (event) {
  var selectedFile = event.target.files[0];

  if (!selectedFile) {
    console.log("No file selected.");
    return;
  } // Use FileReader to read the contents of the file as text


  var reader = new FileReader();

  reader.onload = function (event) {
    var csvData = event.target.result;
    console.log("CSV Data as String:", csvData);
    var lines = csvData.split('\n');
    var headings = lines[0].split(',');

    for (var i = 1; i < lines.length; i++) {
      var values = lines[i].split(',');
      var obj = {};

      for (var j = 0; j < headings.length; j++) {
        obj[headings[j]] = values[j];
      }

      resultArray.push(obj);
    }
  };

  reader.readAsText(selectedFile);
});

function createSection(title, elements) {
  var section = document.createElement('div');
  section.style.paddingLeft = '20px';
  section.style.paddingRight = '20px';
  var titleLabel = document.createElement('h2');
  titleLabel.textContent = title;
  section.appendChild(titleLabel);
  elements.forEach(function (element) {
    section.appendChild(element);
    section.appendChild(document.createElement('br'));
  });
  section.appendChild(document.createElement('hr'));
  return section;
} // Append input elements and button to the document body


function sortByFit(posts) {
  var fitArr = [];
  var objectHash = {};

  for (var post in posts) {
    var fitCompany = posts[post]["Battery Fit"] + posts[post]["Company"];
    objectHash[fitCompany] = post;
    fitArr.push(fitCompany);
  }

  fitArr.sort(function (a, b) {
    return a.localeCompare(b);
  });
  var finalMap = {};

  for (var i = 0; i < fitArr.length; i++) {
    finalMap[objectHash[fitArr[i]]] = posts[objectHash[fitArr[i]]];
  }

  return finalMap;
}

