 import { AppBar, Toolbar, Box, Stack, Typography, IconButton, Switch, Divider, Drawer,
	List,
	ListItem,
	ListItemText,
	InputAdornment,
	Checkbox } from "@mui/material";
import React from "react";
import useMediaQuery from "@mui/material/useMediaQuery";
import { useState, useEffect, useCallback } from "react";
import LeftSection from "./LeftSection";
import RightSection from "./RightSection";
import Hero from "./Hero";
import SignUp from "./SignUp";
import Footer from "../Footer";
import OpportunitiesImage from "../Landing/viewposts.png";
import RelationshipImage from "../Landing/discussionpost.png";
import Video from "../Landing/cardiacrehab.mp4"
import SummariesImage from "../Landing/message.png";
import Banner from "./Banner";
import Image from "../Landing/Auditboard.png"
import { styled } from "@mui/material/styles";
import ButtonBase from "@mui/material/ButtonBase";
import { Grid, Paper } from "@mui/material";
import TextField from '@mui/material/TextField';
import firestore from '../../Utilities/firebase';
import { doc, setDoc, collection, getDocs, writeBatch } from "firebase/firestore"; 
import Logo from "../../images/Logo.svg";

import ExpandedCard from "./ExpandedCard";
import SearchIcon from "@mui/icons-material/Search";

import { navigateTo } from "../../Utilities/navigate";
import { useNavigate } from "react-router-dom";

import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import CssBaseline from "@mui/material/CssBaseline";
import Link from "@mui/material/Link";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";
import Container from "@mui/material/Container";
import Modal from "@mui/material/Modal";
import CloseIcon from '@mui/icons-material/Close';

const CustomButton = ({ children, onClick, disabled }) => (
	<ButtonBase
	  onClick={onClick}
	  sx={{
		width: "100%",
		height: "100%",
		textAlign: "center",
		backgroundColor: disabled ? "#ccc" : "#414fb6",
		// "&:hover": {
		//   backgroundColor: disabled ? "#ccc" : "#35409e",
		// },
	  }}
	  disabled={disabled}
	>
	  {children}
	</ButtonBase>
  );

  const CustomButtonForm = ({ children, onClick, disabled }) => (
	<ButtonBase
	  onClick={onClick}
	  type="submit"
	  sx={{
		width: "100%",
		height: "100%",
		textAlign: "center",
		backgroundColor: disabled ? "#ccc" : "#414fb6",
		// "&:hover": {
		//   backgroundColor: disabled ? "#ccc" : "#35409e",
		// },
	  }}
	  disabled={disabled}
	>
	  {children}
	</ButtonBase>
  );

  const fileInput = document.createElement('input');
  fileInput.type = 'file';
  const resultArray = [];

  const weekInput = document.createElement('input');
  weekInput.type = 'text';
  weekInput.placeholder = 'Enter week';

  const startDatetimeInput = document.createElement('input');
  startDatetimeInput.type = 'datetime-local';

  const endDatetimeInput = document.createElement('input');
  endDatetimeInput.type = 'datetime-local';

  // Create a button element to upload data to Firebase
  const uploadButton = document.createElement('button');
  uploadButton.textContent = 'Upload to Firebase';

fileInput.addEventListener("change", function(event) {
    const selectedFile = event.target.files[0];

    if (!selectedFile) {
        console.log("No file selected.");
        return;
    }

    // Use FileReader to read the contents of the file as text
    const reader = new FileReader();

    reader.onload = function(event) {
        const csvData = event.target.result;
        console.log("CSV Data as String:", csvData);

        const lines = csvData.split('\n');
		const headings = lines[0].split(',');
		for (let i = 1; i < lines.length; i++) {
    		const values = lines[i].split(',');
    		const obj = {};
		    for (let j = 0; j < headings.length; j++) {
  		      obj[headings[j]] = values[j];
   			}
   			resultArray.push(obj);
		}
    };

    reader.readAsText(selectedFile);
});

  function createSection(title, elements) {
	  const section = document.createElement('div');
	  section.style.paddingLeft = '20px';
	  section.style.paddingRight = '20px';

	  const titleLabel = document.createElement('h2');
	  titleLabel.textContent = title;
	  section.appendChild(titleLabel);

	  elements.forEach(element => {
		  section.appendChild(element);
		  section.appendChild(document.createElement('br'));
	  });

	  section.appendChild(document.createElement('hr'));

	  return section;
  }

// Append input elements and button to the document body

function sortByFit(posts) {
	var fitArr = [];
	var objectHash = {};
	for (const post in posts) {
		const fitCompany = posts[post]["Battery Fit"] + posts[post]["Company"];
		objectHash[fitCompany] = post;
		fitArr.push(fitCompany);
	}
	fitArr.sort((a, b) => a.localeCompare(b));
	const finalMap = {};
	for (let i = 0; i < fitArr.length; i++) {
		finalMap[objectHash[fitArr[i]]] = posts[objectHash[fitArr[i]]];
	}
	return finalMap;
}

const ViewPosts = () => {
	const [listings, setListings] = useState(null);
	const [activeId, setActiveId] = useState(null);
	const [currentWeekIndex, setCurrentWeekIndex] = useState(0);
	const [weeks, setWeeks] = useState([]);
	const [startTime, setStartTime] = useState([]);
	const [endTime, setEndTime] = useState([]);
	const [open, setOpen] = React.useState(false);
	const [bidsOpen, setBidsOpen] = React.useState(false);
	const [user, setUser] = React.useState("");
	const [users, setUsers] = React.useState([]);
	const [admin, setAdmin] = React.useState(false);
	const [totalPoints, setTotalPoints] = React.useState(0);
	const [currentPoints, setCurrentPoints] = React.useState(0);
	const [signedIn, setSignedIn] = React.useState(false);
	const [companyBids, setCompanyBids] = React.useState({});
	const [isDrawerOpen, setIsDrawerOpen] = useState(false);
	const [selectedOptions, setSelectedOptions] = useState(["Venture", "Growth", "Buyout"]);
	const [minValues, setMinValues] = useState({});
	const [maxValues, setMaxValues] = useState({});
	const [closed, setClosed] = useState([]);
	const navigate = useNavigate();
	const nodemailer = require('nodemailer');
	const emailCheckInterval = 

  async function uploadCSVDataToFirebase() {
		let listWeeks = [];
		let listStartTimes = [];
		let listEndTimes = [];
		let weekSnapshot = await getDocs(collection(firestore, "weeks"));
		let weekID = "";
		weekSnapshot.forEach(doc => {
			const weekData = doc.data();
			listWeeks = weekData.list;
			listStartTimes = weekData.startTime;
			listEndTimes = weekData.endTime;
			weekID = doc.id;
		})

		const updatedWeeks = [...listWeeks, weekInput.value];
		const updatedStartTime = [...listStartTimes, startDatetimeInput.value];
		const updatedEndTime = [...listEndTimes, endDatetimeInput.value];

		for (const row of resultArray) {
			row["bids"] = { "None": 0 };
			row["week"] = weekInput.value;
			if (row["Battery Fit"] === "Low product fit with battery's interests") {
				row["Battery Fit"] = "3";
			  } else if (row["Battery Fit"] === "Medium product fit with battery's interests") {
				row["Battery Fit"] = "2";
			  } else if (row["Battery Fit"] === "Strong product fit with battery's interests") {
				row["Battery Fit"] = "1";
			  }
			await setDoc(doc(firestore, "companies", row["Company"]), row);
		}
		listWeeks.push(weekInput.value);
		listStartTimes.push(startDatetimeInput.value);
		listEndTimes.push(endDatetimeInput.value);
		await setDoc(doc(firestore, "weeks", weekID), { list: updatedWeeks, startTime: updatedStartTime, endTime: updatedEndTime });
		const emailCheckInterval = setInterval(checkAndSendEmail, 60000);
	}

	const sendBiddingResultsEmail = (usersBidding, listingsBiddings) => {

		const transporter = nodemailer.createTransport({
			// configure your email settings here
		  });

		const mailOptions = {
			from: 'sender@example.com',
			to: 'recipient@example.com',
			subject: 'Bidding Results Update',
			text: `Hi ,

		  The full list of the bidding results is now updated.

		  You won:
		  Congratulations! You have placed the highest bid on the following companies:

		  Switchboard Software, From the team that launched Google BigQuery, Switchboard’s data engineering automation turns disparate data into a single source of truth.
		  You have won with 16 points

		  You lost:
		  You didn't place any unsuccessful bid this week.

		  Complete Bidding Results:
		  · Croissant, Croissant is an application that resales products on behalf of customers.
		  cchow@battery.com won with 3 points

		  · Stylib, Stylib is a platform enabling architects to discover products using AI.
		  cchow@battery.com won with 12 points

		  · Switchboard Software, From the team that launched Google BigQuery, Switchboard’s data engineering automation turns disparate data into a single source of truth.
		  dallin@battery.com won with 16 points

		  · Helpful, Helpful simplifies family caregiving by combining your loved one's insurance benefits and medical records into one user-friendly platform.
		  marcus@battery.com won with 3 points

		  · Effectiv, Fraud detection platform for financial institutions
		  mhoeksema@battery.com won with 5 points

		  · Frigade, Frigade is a developer tool that makes it easy for developers to build better product onboarding and education.
		  mhoeksema@battery.com won with 5 points

		  · Solink, Solink is a provider of video security and business analytics solutions for companies.
		  marcus@battery.com won with 5 points

		  · MSPbots.ai, MSPbots.ai is an IT service company that offers process automation, business intelligence, email support, and concierge service.
		  marcus@battery.com won with 2 points

		  · ReflexAI, ReflexAI provides organizations with tools that transform how they train, develop, and empower their frontline teams.
		  marcus@battery.com won with 2 points

		  Points remaining:
		  You finished this week with 84 bidding points.
		  The points balance will reset back to 100 in 61 days.`
		  };

		  transporter.sendMail(mailOptions, (error, info) => {
			if (error) {
			  console.log('Error sending email:', error);
			} else {
			  console.log('Email sent:', info.response);
			}
		  });

	  };

	const checkAndSendEmail = () => {
		const currentTime = new Date();

		if (currentTime >= endTime) {
		// Call the sendBiddingResultsEmail function with current time and end time
		sendBiddingResultsEmail(users, listings);

		// Stop checking by clearing the interval
		clearInterval(emailCheckInterval);
		}
	};

	// add date query
	// change if admin vs. user
	useEffect(() => {
		const fetchData = async () => {
		  // if (!currentUser) {
		  //   navigate("/signin");
		  // }
		  let weeksSnapshot = await getDocs(collection(firestore, "weeks"));
		  let weeksData = [];
		  let startTimes = [];
		  let endTimes = [];
		  let closedBoolean = [];
		  weeksSnapshot.forEach(weeksSnapshot => {
			const docData = weeksSnapshot.data();
			weeksData = docData.list;
			startTimes = docData.startTime;
			endTimes = docData.endTime;
			closedBoolean = docData.closed;
		  })
		  setWeeks(weeksData);
		  setCurrentWeekIndex(weeksData.length - 1);
		  updateStartAndEndTimes(weeksData.length - 1, startTimes, endTimes);
		  setClosed(closedBoolean);
		};
		fetchData().catch(console.error);
	  }, []);

	  const updateStartAndEndTimes = (index, startTimes, endTimes) => {
		setStartTime(startTimes[index].toDate());
		setEndTime(endTimes[index].toDate());
	  };

	  function setMaxAndMin(data) {
		for (const parameterName in data) {
			if (parameterName.includes(" Min")) {
				console.log("min");
				const realParameterName = parameterName.replace(" Min", "");
				const currentMin = data.get(realParameterName);
				setMinValues((prevMinValues) => ({
					...prevMinValues,
					[realParameterName]: currentMin,
				}));
			}
			if (parameterName.includes(" Max")) {
				const realParameterName = parameterName.replace(" Max", "");
				const currentMax = data.get(realParameterName);
				setMaxValues((prevMaxValues) => ({
					...prevMaxValues,
					[realParameterName]: currentMax,
				}));
			}
		}
	  }

	  function meetParameterMinimums(companyData) {
		for (const parameterName in minValues) {
			const parameterValue = companyData[parameterName];
			const minRequirement = minValues[parameterName];
			if (minRequirement !== undefined && parameterValue < minRequirement) {
				return false; // Parameter value doesn't meet minimum requirement
			}
		}
		return true; // All parameters meet minimum requirements
	  }

	  function meetParameterMaximums(companyData) {
		for (const parameterName in maxValues) {
			const parameterValue = companyData[parameterName];
			const maxRequirement = maxValues[parameterName];
			if (maxRequirement !== undefined && parameterValue > maxRequirement) {
				return false; // Parameter value doesn't meet minimum requirement
			}
		}
		return true; // All parameters meet minimum requirements
	  }

	  function meetCheckbox(companyData) {
		  const parameterValue = companyData["Stage"];
		  for (const option of selectedOptions) {
			console.log(option);
			console.log(parameterValue);
			if (option === parameterValue) {
				return true;
			}
		  }
		return false;
	  }

	  async function fetchDataAndUpdate(event) {
		if(!event) {
			const fetchData = async () => {
				let posts = {};
				let companiesSnapshot = await getDocs(collection(firestore, "companies"));
				let secondaryData = {};
				companiesSnapshot.forEach(companySnapshot => {
				  const companyID = companySnapshot.id;
				  const companyData = companySnapshot.data();
				  let companyDate = companyData.week;
				  secondaryData["# LIEs"] = parseInt(companyData["# LIEs"], 10);
				  secondaryData["Founded"] = parseInt(companyData["Founded"], 10);
				  secondaryData["Last round amt (USD$M)"] = parseFloat(companyData["Last round amt (USD$M)"].replace("$", "").replace(" M", ""))
				  secondaryData["Last round post money valuation (USD$M)"] = parseFloat(companyData["Last round post money valuation (USD$M)"].replace("$", "").replace(" M", ""));
				  secondaryData["Total amount raised to-date (USD$M)"] = parseFloat(companyData["Total amount raised to-date (USD$M)"].replace("$", "").replace(" M", ""));
				  secondaryData["Growth YoY"] = parseFloat(companyData["Growth YoY"].replace("%", ""));
				  if (companyDate === weeks[currentWeekIndex] && meetParameterMaximums(secondaryData) && meetParameterMinimums(secondaryData) && meetCheckbox(companyData)) {
					posts[companyID] = companyData;
					posts[companyID]["newBidValue"] = 0;
				  }
				});

				posts = sortByFit(posts);
				setActiveId(Object.keys(posts)[0]);
				setListings(posts);

				let open = (getCurrentTimeRemaining() !== "0hr, 0min, 0sec") && (currentWeekIndex === weeks.length - 1);
				setBidsOpen(open);
			  };
			  fetchData().catch(console.error);
		} else {
			const data = new FormData(event.currentTarget);
			const fetchData = async () => {
			let posts = {};
			let companiesSnapshot = await getDocs(collection(firestore, "companies"));
			let secondaryData = {};
			await setMaxAndMin(data);
			console.log(maxValues);
			console.log(minValues);
			companiesSnapshot.forEach(companySnapshot => {
				const companyID = companySnapshot.id;
				const companyData = companySnapshot.data();
				let companyDate = companyData.week;
				secondaryData["# LIEs"] = parseInt(companyData["# LIEs"], 10);
				secondaryData["Founded"] = parseInt(companyData["Founded"], 10);
				secondaryData["Last round amt (USD$M)"] = parseFloat(companyData["Last round amt (USD$M)"].replace("$", "").replace(" M", ""))
				secondaryData["Last round post money valuation (USD$M)"] = parseFloat(companyData["Last round post money valuation (USD$M)"].replace("$", "").replace(" M", ""));
				secondaryData["Total amount raised to-date (USD$M)"] = parseFloat(companyData["Total amount raised to-date (USD$M)"].replace("$", "").replace(" M", ""));
				secondaryData["Growth YoY"] = parseFloat(companyData["Growth YoY"].replace("%", ""));
				if (companyDate === weeks[currentWeekIndex] && meetParameterMaximums(secondaryData) && meetParameterMinimums(secondaryData) && meetCheckbox(companyData)) {
				posts[companyID] = companyData;
				posts[companyID]["newBidValue"] = 0;
				}
			});

			posts = sortByFit(posts);
			setActiveId(Object.keys(posts)[0]);
			setListings(posts);

			let open = (getCurrentTimeRemaining() !== "0hr, 0min, 0sec") && (currentWeekIndex === weeks.length - 1);
			setBidsOpen(open);
			};

			fetchData().catch(console.error);
		}
	  }

	  useEffect(() => {
		fetchDataAndUpdate();
	  }, [currentWeekIndex]);

	  async function handleSignIn(event) {
		console.log("signin");
		event.preventDefault();
		const data = new FormData(event.currentTarget);
		let userData = {};
		let usersSignIn = [];
		let userSnapshot = await getDocs(collection(firestore, "users"));
		let userID = "";
		userSnapshot.forEach(doc => {
		  const uData = doc.data();
		  userID = doc.id;
		  userData[userID] = uData;
		  usersSignIn.push(uData);
		});
		setUsers(usersSignIn);
		// Iterate over the properties of the userData object
		Object.keys(userData).forEach(userID => {
		  const userFirestore = userData[userID];
		  if (data.get("username") === userFirestore.username) {
			setAdmin(userFirestore.permissions === "admin" ? true : false);
			setUser(userFirestore.username);
			setTotalPoints(userFirestore.points);
			setCurrentPoints(userFirestore.currentWeek);
			setSignedIn(true);
		  }
		});
	  }

	  const handlePreviousWeek = () => {
		if (currentWeekIndex > 0) {
		  setCurrentWeekIndex(currentWeekIndex - 1);
		}
	  };

	  const handleNextWeek = () => {
		if (currentWeekIndex < weeks.length - 1) {
		  setCurrentWeekIndex(currentWeekIndex + 1);
		}
	  };

	// const handleTogglePosts = () => {
	// 	setTogglePosts(!togglePosts);
	// };

	const handleClose = () => {
		setOpen(false);
	};

	const handleChange = event => {
		const result = event.target.value;

	}

	const handleNavigateHome = () => {
		navigate("/");
	};

	const handleChangeUserStatus = async () => {
		setSignedIn(false);
	};

	const getCurrentTimeRemaining = () => {
		if (startTime && endTime) {
		  const currentTime = new Date();
		  if (currentTime >= startTime && currentTime <= endTime) {
			const timeRemaining = endTime - currentTime;
			const hours = Math.floor(timeRemaining / (60 * 60 * 1000));
			const minutes = Math.floor(
			  (timeRemaining % (60 * 60 * 1000)) / (60 * 1000)
			);
			const seconds = Math.floor((timeRemaining % (60 * 1000)) / 1000);
			return `${hours}hr, ${minutes}min, ${seconds}sec`;
		  }
		}
		return "0hr, 0min, 0sec";
	  };

	  const getCurrentPointsRemaining = () => {
		return(currentPoints);
	  };

	  const getTotalPointsRemaining = () => {
		return(totalPoints);
	  };

	  const Img = styled("img")({
		margin: "auto",
		display: "block",
		maxWidth: "100%",
		maxHeight: "100%",
	});

	  const ListingCard = ({ listing, isActive, clickFunction, open, isExpanded, onExpandToggle }) => {

		return (
			<Box
			  sx={{
				justifyContent: "space-between",
				width: "1160px",
				borderRadius: "30px",
				px: "14px",
				py: isExpanded ? "12px" : "4px",
				"&.Mui-active": {
				  border: "4px solid #482FD7",
				},
				alignItems: "center"
			  }}
			  onClick={onExpandToggle}
			>
			  <Box
				  flex={1}
				  sx={{
					display: "flex",
					flexDirection: "horizontal",
					justifyContent: "space-between",
					height: "100%", 
					alignItems: "center"
				  }}
				>
				<Box
				  flex={1}
				  sx={{
					display: "flex",
					flexDirection: "horizontal",
					height: "100%", 
					alignItems: "center",
					maxWidth: 788
				  }}
				>
				<Box
					width={88}
					height={88}
					   sx={{
						  display: "flex",
						  justifyContent: "flex-start",
						  alignItems: "center",
					}}
				  >
					<Img
						alt={listing.Company}
						  src={listing["Image\r"]}
						  sx={{ maxWidth: 88, maxHeight: 88, width: "auto", height: "auto", borderRadius: "5px" }}
					/>
				  </Box>
					<Box
					  flex={1}
