				console.log("handled");
				event.preventDefault();
				const data = new FormData(event.currentTarget);
				let currPoints = 0;
				for (const listing of listings) {
					currPoints += data.get(listing.Company);
				}

				if (currPoints > totalPoints) {
				  alert("Invalid points for the bids.");
				} else {
				  const companiesSnapshot = await getDocs(collection(firestore, "companies"));

				  const batch = writeBatch(firestore);

				  companiesSnapshot.forEach((companyDoc) => {
					const companyName = companyDoc.data().Company;
					const companyBid = data.get(companyName);

					if (companyBid !== undefined) {
					  const bidsRef = doc(firestore, "companies", companyDoc.id);
					  const bidsData = companyDoc.data().bids;
					  const updatedBids = {...bidsData, [user]: companyBid,};
					  batch.update(bidsRef, { bids: updatedBids });
					} else {
					  const bidsRef = doc(firestore, "companies", companyDoc.id);
					  const bidsData = companyDoc.data().bids;
					  const updatedBids = {...bidsData, [user]: 0,};
					  batch.update(bidsRef, { bids: updatedBids });
					}
				  });
				  let userSnapshot = await getDocs(collection(firestore, "users"));
				  let userID = "";
					userSnapshot.forEach(doc => {
					const userData = doc.data();
					if (user === userData.username) {
						userID = doc.id;
					}
				})
				  batch.update(doc(firestore, "users", userID), { currentWeek: currPoints });
