import React from "react";
import { useState, useEffect } from "react";
import { styled } from "@mui/material/styles";
import "./Main.css";

import GitHubIcon from "./GitHub.png";
import LinkedInIcon from "./LinkedInLogo.png";
import HomeLogo from "./Logo.png";

const WORK_DURATION = 25 * 60;
const SHORT_BREAK_DURATION = 5 * 60;
const LONG_BREAK_DURATION = 10 * 60;

const ImgNoMargin = styled("img")({
	maxWidth: "100%",
	maxHeight: "100%",
});

const Planner = () => {
	const [timer, setTimer] = useState(WORK_DURATION);
  	const [timerRunning, setTimerRunning] = useState(false);
	const [selectedTimerOption, setSelectedTimerOption] = useState('work');

	const updateTimer = () => {
		switch (selectedTimerOption) {
			case 'work':
				setTimer(WORK_DURATION);
				break;
			case 'shortBreak':
				setTimer(SHORT_BREAK_DURATION);
				break;
			case 'longBreak':
				setTimer(LONG_BREAK_DURATION);
				break;
			default:
				break;
		}
	};

	const startTimer = () => {
		setTimerRunning(true);
	};

	const stopTimer = () => {
		setTimerRunning(false);
	};

	const handleTick = () => {
		if (timer > 0) {
			setTimer((prevTimer) => prevTimer - 1);
		} else {
			setTimerRunning(false);
		}
	};

	useEffect(() => {
		let timerInterval;

		if (timerRunning) {
			timerInterval = setInterval(handleTick, 1000);
		}

		return () => clearInterval(timerInterval);
	}, [timerRunning, timer]);

	const handleTimerToggle = () => {
		setTimerRunning((prevRunning) => !prevRunning);
	};

	const handleTimerOptionChange = (newOption) => {
		setTimerRunning(false);
		setSelectedTimerOption(newOption);
		updateTimer();
	};

	const formatTime = (seconds) => {
		const minutes = Math.floor(seconds / 60);
		const remainingSeconds = seconds % 60;
		return `${String(minutes).padStart(2, '0')}:${String(remainingSeconds).padStart(2, '0')}`;
	};

	const [mood, setMood] = useState(3);

	const [sleepHours, setSleepHours] = useState(7);

	const [topLeftChecklist, setTopLeftChecklist] = useState([
		{ id: 1, label: 'Task 1', checked: false },
		{ id: 2, label: 'Task 2', checked: false },
	]);

	const ChecklistItem = ({ label, checked, onToggle }) => {
		return (
			<div className="checklist-item">
				<input type="checkbox" checked={checked} onChange={onToggle} />
				<label>{label}</label>
			</div>
		);
	};

	const handleToggle = (list, setList, itemId) => {
		setList((prevList) =>
			prevList.map((item) =>
			item.id === itemId ? { ...item, checked: !item.checked } : item
			)
		);
	};

    return (
		<div className="view">
			<div 
				style={{
					backgroundColor: "#f0fff1",
					minWidth: "calc(100vw - 40px)",
					minHeight: "calc(100vh - 40px)",
					position: "relative",
					overflow: "hidden"
				}}
			>
				<div
					style={{
						zIndex: 2,
						display: "flex",
						justifyContent: "space-between",
						alignItems: "center",
						width: "calc(100vw - 160px)",
						backgroundColor: "#f0fff1",
						padding: "15px 60px 15px",
						position: "fixed"
					}}
				>
					<a href="/">
						<img src={HomeLogo} width={"60px"} alt='Logo' />
					</a>
					<div
						style={{
							display: "flex",
							alignItems: "center"
						}}
					>	
						<a href="https://github.com/mkim45" target="_blank" rel="noopener noreferrer" underline="none">
							<ImgNoMargin
								alt="githubtop"
								src={GitHubIcon}
								sx={{ height: "40px", width: "40px", paddingRight: "20px" }}
							/>
						</a>
						<a href="https://www.linkedin.com/in/michael-kim-nu/" target="_blank" rel="noopener noreferrer" underline="none">
							<ImgNoMargin
								alt="linkedintop"
								src={LinkedInIcon}
								sx={{ height: "40px", width: "40px" }}
							/>
						</a>
					</div>
				</div>
				<div 
					style={{ 
						marginLeft: "40px",
						marginRight: "40px"
					}}
				>
					<div className="planner-container">
						<div className="checklist-container">
							<h2>Checklist Header</h2>
							{topLeftChecklist.map((item) => (
								<ChecklistItem
									key={item.id}
									label={item.label}
									checked={item.checked}
									onToggle={() => handleToggle(topLeftChecklist, setTopLeftChecklist, item.id)}
								/>
							))}
						</div>
						{/* Top right - Timer */}
						<div className="timer-container">
							<h2>Timer</h2>
							<div className="timer-controls">
								<button onClick={() => { handleTimerOptionChange('work') }}>Work</button>
								<button onClick={() => { handleTimerOptionChange('shortBreak') }}>Short Break</button>
								<button onClick={() => { handleTimerOptionChange('longBreak') }}>Long Break</button>
								<button onClick={handleTimerToggle}>{timerRunning ? 'Stop' : 'Start'}</button>
							</div>
							<div className="timer-display">{formatTime(timer)}</div>
						</div>

						{/* Bottom left - Big Checklist */}
						<div className="big-checklist-container">
							<h2>Big Checklist</h2>
							{/* Add your big checklist component here */}
						</div>

						{/* Bottom right - Mood, Sleep, Time Breakdown */}
						<div className="bottom-right-container">
							<div className="mood-container">
							<h2>Mood Scale</h2>
							{/* Add your mood scale component here */}
							</div>
							<div className="sleep-container">
							<h2>Sleep Hours</h2>
							{/* Add your sleep input component here */}
							</div>
							<div className="time-breakdown-container">
							<h2>Time Breakdown</h2>
							{/* Add your time breakdown input components here */}
							</div>
						</div>
					</div>
				</div>
			</div>
		</div>
    );
};

export default Planner;
