import { AppBar, Toolbar, Box, Stack, Typography, IconButton, Switch, Divider, Drawer,
	List,
	ListItem,
	ListItemText,
	InputAdornment,
	Checkbox } from "@mui/material";
import React from "react";
import useMediaQuery from "@mui/material/useMediaQuery";
import { useState, useEffect, useCallback } from "react";
import LeftSection from "./LeftSection";
import RightSection from "./RightSection";
import Hero from "./Hero";
import SignUp from "./SignUp";
import Footer from "../Footer";
import OpportunitiesImage from "../Landing/viewposts.png";
import RelationshipImage from "../Landing/discussionpost.png";
import Video from "../Landing/cardiacrehab.mp4"
import SummariesImage from "../Landing/message.png";
import Banner from "./Banner";
import Image from "../Landing/Auditboard.png"
import { styled } from "@mui/material/styles";
import ButtonBase from "@mui/material/ButtonBase";
import { Grid, Paper } from "@mui/material";
import TextField from '@mui/material/TextField';
import firestore from '../../Utilities/firebase';
import { doc, setDoc, collection, getDocs, writeBatch } from "firebase/firestore"; 
import Logo from "../../images/Logo.svg";

import Portrait from "./Michael_Kim.jpg";

import ExpandedCard from "./ExpandedCard";
import SearchIcon from "@mui/icons-material/Search";

import { navigateTo } from "../../Utilities/navigate";
import { useNavigate, Link } from "react-router-dom";

import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import CssBaseline from "@mui/material/CssBaseline";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";
import Container from "@mui/material/Container";
import Modal from "@mui/material/Modal";
import CloseIcon from '@mui/icons-material/Close';
import emailjs from '@emailjs/browser';

const ViewPosts = () => {
	const [textOptionOneIndex, setTextOptionOneIndex] = useState(0);
	const [textOptionTwoIndex, setTextOptionTwoIndex] = useState(0);
    const navigate = useNavigate();

	const rotatingTextOptionsOne = [
		"software engineering",
		"machine learning",
		"web development",
		"data analysis"
	];

	const rotatingTextOptionsTwo = [
		"proteomics",
		"bioinformatics",
		"business",
		"metabolomics"
	];

	const Img = styled("img")({
		margin: "auto",
		display: "block",
		maxWidth: "100%",
		maxHeight: "100%",
	});

	const textStyle = {
		fontFamily: '"Rosart", "Georgia", "Times New Roman", "FZNewBaoSong", serif',
		fontWeight: 600,
		color: "#1e695e",
		fontSize: 40,
		lineHeight: "1.5",
		verticalAlign: "text-bottom",
		letterSpacing: "0.5px",
    };

    const activeStyle = {
		fontFamily: '"Rosart", "Georgia", "Times New Roman", "FZNewBaoSong", serif',
		fontWeight: 600,
		color: "#1e695e",
		fontSize: 40,
		lineHeight: "1.5",
		verticalAlign: "text-bottom",
		letterSpacing: "0.5px",
		paddingLeft: 1.25,
        opacity: 1,
        visibility: 'visible',
    };

	useEffect(() => {
		const intervalOne = setInterval(() => {
			setTextOptionOneIndex((prevIndex) => (prevIndex + 1) % rotatingTextOptionsOne.length);
		}, 5000);

		const intervalTwo = setInterval(() => {
			setTextOptionTwoIndex((prevIndex) => (prevIndex + 1) % rotatingTextOptionsTwo.length);
		}, 5000 + 2500);

		return () => {
			clearInterval(intervalOne);
			clearInterval(intervalTwo);
		};
	}, []);

    return (
        <div 
			style={{
				backgroundColor: "#fbe4d8",
				width: "100vw",
				height: "100vh",
				overflowX: "hidden"
			}}>
			<CssBaseline />
			<style>
				{`
					body {
						margin: 0;
						background-color: #fbe4d8;
					}
				`}
			</style>
            <AppBar
				position="fixed"
				sx={{ zIndex: 1400, 
					  height: 96,
					  boxShadow: "none",
					  overflow: "visible",
					  backgroundColor: "#fbe4d8"
				}}
			>
				<Container maxWidth='xl'>
					<Toolbar sx={{ justifyContent: "space-between" }}>
						<Box sx={{ display: { xs: "none", md: "flex" }, pt: 4, pb: 3 }}>
							<img src={Logo} width={140} alt='Logo' />
						</Box>
						<Stack
							sx={{ flexGrow: 0, display: { xs: "none", md: "flex" }, pt: 4, pb: 3 }}
							direction='row'
							>
								<Typography
									variant='body3'
									noWrap
									component='a'
									sx={{
										mr: 8,
										display: "flex",
										fontFamily: '"Rosart", "Georgia", "Times New Roman", "FZNewBaoSong", serif',
										fontWeight: 500,
										color: "#190019",
										textDecoration: "none",
										flexGrow: 1,
										cursor: "pointer",
									}}>
									ABOUT ME
								</Typography>
								<Typography
									variant='body3'
									noWrap
									component='a'
									sx={{
										mr: 8,
										display: "flex",
										fontFamily: '"Rosart", "Georgia", "Times New Roman", "FZNewBaoSong", serif',
										fontWeight: 500,
										color: "#190019",
										textDecoration: "none",
										flexGrow: 1,
										cursor: "pointer",
									}}>
									PROJECTS
								</Typography>
								<Typography
									variant='body3'
									noWrap
									component='a'
									sx={{
										mr: 8,
										display: "flex",
										fontFamily: '"Rosart", "Georgia", "Times New Roman", "FZNewBaoSong", serif',
										fontWeight: 500,
										color: "#190019",
										textDecoration: "none",
										flexGrow: 1,
										cursor: "pointer",
									}}>
									EXPERIENCES
								</Typography>
								<Typography
									variant='body3'
									noWrap
									component='a'
									sx={{
										display: "flex",
										fontFamily: '"Rosart", "Georgia", "Times New Roman", "FZNewBaoSong", serif',
										fontWeight: 500,
										color: "#190019",
										textDecoration: "none",
										flexGrow: 1,
										cursor: "pointer",
									}}>
