sourcesContent:
 import { AppBar, Toolbar, Box, Stack, Typography, IconButton, Switch, Divider, Drawer,
	List,
	ListItem,
	ListItemText,
	InputAdornment,
	Checkbox } from "@mui/material";
import React from "react";
import useMediaQuery from "@mui/material/useMediaQuery";
import { useState, useEffect, useCallback } from "react";
import LeftSection from "./LeftSection";
import RightSection from "./RightSection";
import Hero from "./Hero";
import SignUp from "./SignUp";
import Footer from "../Footer";
import OpportunitiesImage from "../Landing/viewposts.png";
import RelationshipImage from "../Landing/discussionpost.png";
import Video from "../Landing/cardiacrehab.mp4"
import SummariesImage from "../Landing/message.png";
import Banner from "./Banner";
import Image from "../Landing/Auditboard.png"
import { styled } from "@mui/material/styles";
import ButtonBase from "@mui/material/ButtonBase";
import { Grid, Paper } from "@mui/material";
import TextField from '@mui/material/TextField';
import firestore from '../../Utilities/firebase';
import { doc, setDoc, collection, getDocs, writeBatch } from "firebase/firestore"; 
import Logo from "../../images/Logo.svg";

import ExpandedCard from "./ExpandedCard";
import SearchIcon from "@mui/icons-material/Search";

import { navigateTo } from "../../Utilities/navigate";
import { useNavigate, Link } from "react-router-dom";

import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import CssBaseline from "@mui/material/CssBaseline";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";
import Container from "@mui/material/Container";
import Modal from "@mui/material/Modal";
import CloseIcon from '@mui/icons-material/Close';
import emailjs from '@emailjs/browser';

const CustomButton = ({ children, onClick, disabled }) => (
	<ButtonBase
	  onClick={onClick}
	  sx={{
		width: "100%",
		height: "100%",
		textAlign: "center",
		backgroundColor: disabled ? "#ccc" : "#414fb6",
		// "&:hover": {
		//   backgroundColor: disabled ? "#ccc" : "#35409e",
		// },
	  }}
	  disabled={disabled}
	>
	  {children}
	</ButtonBase>
  );

  const CustomButtonGray = ({ children, onClick, disabled }) => (
	<ButtonBase
	  onClick={onClick}
	  sx={{
		width: "100%",
		height: "100%",
		textAlign: "center",
		backgroundColor: "#6C757D",
		// "&:hover": {
		//   backgroundColor: disabled ? "#ccc" : "#35409e",
		// },
	  }}
	  disabled={disabled}
	>
	  {children}
	</ButtonBase>
  );

  const CustomButtonStage = ({ children, disabled, stage }) => (
	<ButtonBase
	  sx={{
		width: "100%",
		height: "100%",
		textAlign: "center",
		backgroundColor: stage === "Venture" ? "#414fb6" : stage === "Growth" ? "#4FB641" : "#B6414F"
		// "&:hover": {
		//   backgroundColor: disabled ? "#ccc" : "#35409e",
		// },
	  }}
	  disabled={disabled}
	>
	  {children}
	</ButtonBase>
  );

  const CustomButtonForm = ({ children, onClick, disabled }) => (
	<ButtonBase
	  onClick={onClick}
	  type="submit"
	  sx={{
		width: "100%",
		height: "100%",
		textAlign: "center",
		backgroundColor: disabled ? "#ccc" : "#414fb6",
		// "&:hover": {
		//   backgroundColor: disabled ? "#ccc" : "#35409e",
		// },
	  }}
	  disabled={disabled}
	>
	  {children}
	</ButtonBase>
  );

  const fileInput = document.createElement('input');
  fileInput.type = 'file';
  const resultArray = [];

  const weekInput = document.createElement('input');
  weekInput.type = 'text';
  weekInput.placeholder = 'Enter week';

  const startDatetimeInput = document.createElement('input');
  startDatetimeInput.type = 'datetime-local';

  const endDatetimeInput = document.createElement('input');
  endDatetimeInput.type = 'datetime-local';

  // Create a button element to upload data to Firebase
  const uploadButton = document.createElement('button');
  uploadButton.textContent = 'Upload to Firebase';

fileInput.addEventListener("change", function(event) {
    const selectedFile = event.target.files[0];

    if (!selectedFile) {
        console.log("No file selected.");
        return;
    }

    // Use FileReader to read the contents of the file as text
    const reader = new FileReader();

    reader.onload = function(event) {
        const csvData = event.target.result;
        console.log("CSV Data as String:", csvData);

        const lines = csvData.split('\n');
		const headings = lines[0].split(',');
		for (let i = 1; i < lines.length; i++) {
    		const values = lines[i].split(',');
    		const obj = {};
		    for (let j = 0; j < headings.length; j++) {
  		      obj[headings[j]] = values[j];
   			}
   			resultArray.push(obj);
		}
    };

    reader.readAsText(selectedFile);
});

  function createSection(title, elements) {
	  const section = document.createElement('div');
	  section.style.paddingLeft = '20px';
	  section.style.paddingRight = '20px';

	  const titleLabel = document.createElement('h2');
	  titleLabel.textContent = title;
	  section.appendChild(titleLabel);

	  elements.forEach(element => {
		  section.appendChild(element);
		  section.appendChild(document.createElement('br'));
	  });

	  section.appendChild(document.createElement('hr'));

	  return section;
  }

// Append input elements and button to the document body

function sortByFit(posts) {
	var fitArr = [];
	var objectHash = {};
	for (const post in posts) {
		const fitCompany = posts[post]["Battery Fit"] + posts[post]["Company"];
		objectHash[fitCompany] = post;
		fitArr.push(fitCompany);
	}
	fitArr.sort((a, b) => a.localeCompare(b));
	const finalMap = {};
	for (let i = 0; i < fitArr.length; i++) {
		finalMap[objectHash[fitArr[i]]] = posts[objectHash[fitArr[i]]];
	}
	return finalMap;
}

const ViewPosts = () => {
	const [listings, setListings] = useState(null);
	const [activeId, setActiveId] = useState(null);
	const [currentWeekIndex, setCurrentWeekIndex] = useState(0);
	const [weeks, setWeeks] = useState([]);
	const [startTime, setStartTime] = useState([]);
	const [endTime, setEndTime] = useState([]);
	const [open, setOpen] = React.useState(false);
	const [bidsOpen, setBidsOpen] = React.useState(false);
	const [user, setUser] = React.useState("");
	const [users, setUsers] = React.useState([]);
	const [admin, setAdmin] = React.useState(false);
	const [totalPoints, setTotalPoints] = React.useState(0);
	const [currentPoints, setCurrentPoints] = React.useState(0);
	const [signedIn, setSignedIn] = React.useState(false);
	const [companyBids, setCompanyBids] = React.useState({});
	const [isDrawerOpen, setIsDrawerOpen] = useState(false);
	const [selectedOptions, setSelectedOptions] = useState(["Venture", "Growth", "Buyout"]);
	const [minValues, setMinValues] = useState({});
	const [maxValues, setMaxValues] = useState({});
	const [closed, setClosed] = useState([]);
	const navigate = useNavigate();
	const [emailCheckInterval, setEmailCheckInterval] = useState(null);

	async function uploadCSVDataToFirebase(event) {
		console.log("started");
		event.preventDefault();
		const data = new FormData(event.currentTarget);
		const selectedFile = data.get("file");
		const reader = new FileReader();

		reader.onload = function(event) {
			const csvData = event.target.result;
			console.log("CSV Data as String:", csvData);

			const lines = csvData.split('\n');
			const headings = lines[0].split(',');
			for (let i = 1; i < lines.length; i++) {
				const values = lines[i].split(',');
				const obj = {};
				for (let j = 0; j < headings.length; j++) {
				obj[headings[j]] = values[j];
				}
				resultArray.push(obj);
			}
		};

		reader.readAsText(selectedFile);

		console.log(resultArray);

		let listWeeks = [];
		let listStartTimes = [];
		let listEndTimes = [];
		let weekSnapshot = await getDocs(collection(firestore, "weeks"));
		let weekID = "";
		weekSnapshot.forEach(doc => {
			const weekData = doc.data();
			listWeeks = weekData.list;
			listStartTimes = weekData.startTime;
			listEndTimes = weekData.endTime;
			weekID = doc.id;
		})

		const updatedWeeks = [...listWeeks, data.get("week")];
		const updatedStartTime = [...listStartTimes, Date.parse(data.get("start"))];
		const updatedEndTime = [...listEndTimes, Date.parse(data.get("end"))];

		for (const row of resultArray) {
			row["bids"] = { "None": 0 };
			row["week"] = data.get("week");
			if (row["Battery Fit"] === "Low product fit with battery's interests") {
				row["Battery Fit"] = "3";
			} else if (row["Battery Fit"] === "Medium product fit with battery's interests") {
				row["Battery Fit"] = "2";
			} else if (row["Battery Fit"] === "Strong product fit with battery's interests") {
				row["Battery Fit"] = "1";
			}
			await setDoc(doc(firestore, "companies", row["Company"]), row);
		}
		console.log("middle");
		listWeeks.push(data.get("week"));
		listStartTimes.push(Date.parse(data.get("start")));
		listEndTimes.push(Date.parse(data.get("end")));
		await setDoc(doc(firestore, "weeks", weekID), { list: updatedWeeks, startTime: updatedStartTime, endTime: updatedEndTime });
		let companiesSnapshot = await getDocs(collection(firestore, "companies"));
		let posts = {};
		companiesSnapshot.forEach(doc => {
			const companyData = doc.data();
			if (companyData.week === data.get("week")) {
				posts[companyData.Company] = companyData;
			}
		})
		// console.log("announcement");
		// checkAndSendAnnouncement(posts, data.get("week"));
		// console.log("results");
		// setEmailCheckInterval(
		// 	setInterval(() => {
		// 		checkAndSendResults(posts, Date.parse(data.get("end")), data.get("week"), emailCheckInterval);
		// 	  }, 60000)
		//   );
		// console.log("finished");
	}

	const sendBiddingAnnouncementEmail = (listings, week) => {
		for (const currUser of users) {

			let strongFitsText = "";
			let moderateFitsText = "";
			let lowFitsText = "";

			for (const post in listings) {
				console.log(listings[post]);
				if (listings[post]["Battery Fit"] === "1") {
					strongFitsText = strongFitsText + `<ul type="disc">
					<li><strong><a href="${listings[post].Website}" target="_blank">${listings[post].Company}</a></strong>, ${listings[post]["One-Liner"]}<ul type="circle">
							<li><u>Stage:</u> ${listings[post]["Stage"]}. Founded in ${listings[post]["Founded"]} with ${listings[post]["Total amount raised to-date (USD$M)"]} total raised-to-date.</li>
						</ul>
					</li>
				</ul>`
				} else if (listings[post]["Battery Fit"] === "2") {
					moderateFitsText = moderateFitsText + `<ul type="disc">
					<li><strong><a href="${listings[post].Website}" target="_blank">${listings[post].Company}</a></strong>, ${listings[post]["One-Liner"]}<ul type="circle">
							<li><u>Stage:</u> ${listings[post]["Stage"]}. Founded in ${listings[post]["Founded"]} with ${listings[post]["Total amount raised to-date (USD$M)"]} total raised-to-date.</li>
						</ul>
					</li>
				</ul>`
				} else {
					lowFitsText = lowFitsText + `<ul type="disc">
					<li><strong><a href="${listings[post].Website}" target="_blank">${listings[post].Company}</a></strong>, ${listings[post]["One-Liner"]}<ul type="circle">
							<li><u>Stage:</u> ${listings[post]["Stage"]}. Founded in ${listings[post]["Founded"]} with ${listings[post]["Total amount raised to-date (USD$M)"]} total raised-to-date.</li>
						</ul>
					</li>
				</ul>`
				}
			}

			const htmlEmail = `<div>
				<h3>Hi ${currUser.username},</h3>
				<p>The full list of the extern sourced companies is now updated on Project_Name: <br><a href="https://charger.battery.com/home/weekly-bidding" target="_blank">Project_Name Weekly Bidding</a></p>` +
				`<h4><u>Strong Battery Fits</u></h4>` + strongFitsText + `<h4><u>Moderate Battery Fits</u></h4>` + moderateFitsText + `<h4><u>Low Battery Fits</u></h4>` + lowFitsText + 
				`<p><br><br>Generated by Battery Project_Name.</p>
			</div>`

			emailjs.send('service_yk5wtav', 'template_4ek81c7', {email: currUser.email, my_html: htmlEmail, email_subject: `Project_Name Extern Companies Announcements - ${week}`}, '3OoIltRjU_ltyV6VG')
			.then((result) => {
				console.log(result.text);
			}, (error) => {
				console.log(error.text);
			});
		} 
	  };

	const sendBiddingResultsEmail = (listings, week) => {
		for (const currUser of users) {
			let successfulBids = [];
			let unsuccessfulBids = [];

			let successfulBidsText = "";
			let unsuccessfulBidsText = "";
			let listingsText = "";

			let totalPointsUsed = 0;

			for (const post in listings) {
				let maxBidUsername = null;
				let maxBidValue = -Infinity;
				let bidPlaced = false;
				for (const [username, bidValue] of Object.entries(listings[post]["bids"])) {
					if (bidValue > maxBidValue) {
						maxBidUsername = username;
						maxBidValue = bidValue;
					}
					if (username === currUser.username) {
						bidPlaced = true;
					}
				}
				if (maxBidUsername === currUser.username) {
					totalPointsUsed += maxBidValue;
					successfulBids.push(listings[post]);
					successfulBidsText = successfulBidsText + `<ul type="disc">
					<li><strong><a href="${listings[post].Website}" target="_blank">${listings[post].Company}</a></strong>, ${listings[post]["One-Liner"]}<ul type="circle">
							<li>You have won with ${maxBidValue} points.</li>
						</ul>
					</li>
				</ul>`
				} else if (bidPlaced) {
					unsuccessfulBids.push(listings[post]);
					unsuccessfulBidsText = unsuccessfulBidsText + `<ul type="disc">
					<li><strong><a href="${listings[post].Website}" target="_blank">${listings[post].Company}</a></strong>, ${listings[post]["One-Liner"]}<ul type="circle">
							<li>You have lost with ${maxBidValue} points.</li>
						</ul>
					</li>
				</ul>`
				}
				if (maxBidUsername !== "None") {
					listingsText = listingsText + `<p>&middot;&nbsp;&nbsp;<strong><a href="${listings[post]["Website"]}" target="_blank">${listings[post].Company}</a></strong>, ${listings[post]["One-Liner"]}</p>
						<ul type="disc">
							<li>${maxBidUsername} won with ${maxBidValue} points.</li>
						</ul>`;
				}
			}

				const winningBids = successfulBids.length === 0 
					? `<h4><u>You won:</u></h4><p>You didn&apos;t place any successful bid this week.</p>`
					: `<h4><u>You won:</u></h4><p>Congratulations! You have placed the highest bid on the following companies:</p>` + successfulBidsText;

				const losingBids = unsuccessfulBids.length === 0 
					? `<h4><u>You lost:</u></h4><p>You didn&apos;t place any unsuccessful bid this week.</p>`
					: `<h4><u>You lost:</u></h4><p>You did not place the highest bid on the following companies:</p>` + unsuccessfulBidsText;

				const htmlEmail = `<div>
				<h3>Hi ${currUser.username},</h3>
				<p>The full list of the bidding results is now updated on Project_Name: <br><a href="https://charger.battery.com/home/weekly-bidding" target="_blank">Project_Name Weekly Bidding</a></p>` + winningBids + losingBids + 
