			newMinValues[realParameterName] = Number(value);
		  }

		  if (parameterName.includes(" Max")) {
			const realParameterName = parameterName.replace(" Max", "");
			newMaxValues[realParameterName] = Number(value);
		  }
		}
		setMinValues(newMinValues);
		setMaxValues(newMaxValues);
	  }

