import * as React from "react";
import Avatar from "@mui/material/Avatar";
import Button from "@mui/material/Button";
import CssBaseline from "@mui/material/CssBaseline";
import TextField from "@mui/material/TextField";
import Link from "@mui/material/Link";
import {Link as RouterLink, Route} from "react-router-dom";
import Grid from "@mui/material/Grid";
import Box from "@mui/material/Box";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";
import Typography from "@mui/material/Typography";
import Container from "@mui/material/Container";
import { signUp, getCurrentUser } from '../utilities/firebase'
import { useNavigate } from 'react-router-dom';
import InboundTermsOfService from "./InboundTOS.jsx";
import InboundPrivacyPolicy from "./PrivacyPolicy.jsx";
import DefaultLayout from "../Components/DefaultLayout";

const Checkbox = props => (
	<input type='checkbox' {...props} />
)

class SignUp extends React.Component {
	constructor(props) {
		super(props);
		this.handleSubmit = this.handleSubmit.bind(this);
	}

	componentDidMount() {
		getCurrentUser().then((user) => {
			if (user != null) {
				this.props.navigate("/settings");
			}
		});
	}

	async handleSubmit(event) {
		event.preventDefault();
		const data = new FormData(event.currentTarget);
		await signUp(data.get("email"), data.get("password"), data.get("lastName"), data.get("firstName"));
		await getCurrentUser().then(async(user) => {
			if (user != null) {
				alert("Your account was created successfully!")
				this.props.navigate("/posts");
			}
		});
	}

	state = { checked: false }

	handleCheckboxChange = event =>
		this.setState({ checked: event.target.checked })

	render() {
		return (
			<Container component='main' maxWidth='xs'>
				<CssBaseline />
				<Box
					sx={{
						marginTop: 8,
						display: "flex",
						flexDirection: "column",
						alignItems: "center",
					}}>
					<Avatar sx={{ m: 1, bgcolor: "primary.main" }}>
						<LockOutlinedIcon />
					</Avatar>
					<Typography component='h1' variant='h5'>
						Sign up
					</Typography>
					<Box
						component='form'
						noValidate
						onSubmit={this.handleSubmit}
						sx={{ mt: 3 }}>
						<Grid container spacing={2}>
							<Grid item xs={12} sm={6}>
								<TextField
									required
									fullWidth
									name='firstName'
									id='firstName'
									label='First Name'
									autoComplete='given-name'
									autoFocus
								/>
							</Grid>
							<Grid item xs={12} sm={6}>
								<TextField
									required
									fullWidth
									name='lastName'
									id='lastName'
									label='Last Name'
									autoComplete='family-name'
								/>
							</Grid>
							<Grid item xs={12}>
								<TextField
