import * as React from 'react';
import Box from '@mui/material/Box';
import { Typography, FormControl, InputLabel, Select, MenuItem } from '@mui/material';
import UserInfo from '../../Pages/DummyData/FakeUserAPI'

const drawerWidth = 240;

const Newsletter = () => {

    const [frequency, setFrequency] = React.useState(UserInfo.newsletter_freq);

    const handleChange = (event) => {
        setFrequency(event.target.value);
    };

    return (
        <Box
            component="main"
            sx={{ flexGrow: 1, p: 1, width: { sm: `calc(100% - ${drawerWidth}px)` } }}
        >
            <Typography variant='h3' mb={2}>Newsletter</Typography>
            <FormControl sx={{ m: 1, minWidth: 220 }} required>
                <InputLabel id="newsletter-settings">Newsletter Frequency</InputLabel>
                <Select
                    labelId="newsletter-frequency-label"
                    id="newsletter-frequency"
                    value={frequency}
                    label="Newsletter Frequency"
                    onChange={handleChange}
                    sx={{
                        minWidth: 300
                    }}
                >
                    <MenuItem value={"none"}>
                        None
                    </MenuItem>
                    <MenuItem value={"daily"}>Daily</MenuItem>
                    <MenuItem value={"weekly"}>Weekly</MenuItem>
                    <MenuItem value={"monthly"}>Monthly</MenuItem>
                </Select>
            </FormControl>
        </Box >
    );
}

export default Newsletter;
