import React, { useState, useEffect } from "react";
import {
  Box,
  Stack,
  Typography,
  IconButton,
  Switch,
} from "@mui/material";
import useMediaQuery from "@mui/material/useMediaQuery";

import ExpandedCard from "../Components/ViewPosts/ExpandedCard";
import ListingsBox from "../Components/ViewPosts/ListingsBox";
import SearchBar from "../Components/ViewPosts/SearchBar";
import SearchIcon from "@mui/icons-material/Search";
import MobileCard from "../Components/ViewPosts/MobileCard";
import loading from "./DummyData/Assets/loading";

import NoPosts from "../Components/ViewPosts/NoPosts";
import FeedbackForm from "../Components/FeedbackForm";
import { useNavigate } from "react-router-dom";

import NoSavedPosts from "../Components/ViewPosts/NoSavedPosts";
import Loading from "../Loading/Loading";

// Simulated "database" of posts
const mockDatabase = {
	posts: {
	  post1: {
		id: "post1",
		title: "Post 1",
		company: "Company A",
		image: "post1_image.jpg",
		locations: ["Location A", "Location B"],
		job_type: "Full-time",
		date_deadline: new Date("2023-08-31"),
		// other properties...
	  },
	  post2: {
		id: "post2",
		title: "Post 2",
		company: "Company B",
		image: "post2_image.jpg",
		locations: ["Location C"],
		job_type: "Part-time",
		date_deadline: new Date("2023-09-15"),
		// other properties...
	  },
	  // ... more posts
	},
	clubs: {
	  club1: {
		name: "Club A",
		logo: "clubA_logo.png",
		posts: ["post1", "post2"],
	  },
	  // ... more clubs
	},
  };

function sortByDate(posts) {
  const sortedPosts = Object.keys(posts).sort(
    (a, b) => posts[a].date_deadline - posts[b].date_deadline
  );
  return sortedPosts.reduce((finalMap, postKey) => {
    finalMap[postKey] = posts[postKey];
    return finalMap;
  }, {});
}

const ViewPosts = () => {
  const [listings, setListings] = useState(null);
  const [activeId, setActiveId] = useState(null);
  const [togglePosts, setTogglePosts] = useState(false);
  const [groupListings, setGroupListings] = useState(null);
  const [groupActiveId, setGroupActiveId] = useState(null);
  const [userExec, setUserExec] = useState(false);
  const [open, setOpen] = useState(false);
  const matches = useMediaQuery("(min-width:900px)");
  const today = new Date();
  const navigate = useNavigate();

  useEffect(() => {
    // Simulated data fetching
    const fetchData = () => {
      // Simulating user data
      const currentUser = {
        clubs: ["club1"],
        exec_for: ["club1"],
      };
      if (!currentUser) {
        navigate("/signin");
      }
      if (currentUser.exec_for.length) {
        setUserExec(true);
      }
      // Simulating data retrieval from the "database"
      const posts = mockDatabase.posts;
      const clubs = mockDatabase.clubs;
	  const updatedPosts = { ...posts };
		for (let clubRef of currentUser.clubs) {
			const club = clubs[clubRef];
			for (let postID of club.posts) {
				const post = posts[postID];
				if (post) {
					if (!updatedPosts[postID].clubs) {
					  updatedPosts[postID].clubs = [];
					}
					updatedPosts[postID].clubs.push({
					  name: club.name,
					  imageUrl: club.logo,
					});
				}
			}
		}
    	setListings(posts);
    	setGroupListings(posts);
    };

    fetchData();
  }, []);

  const handleCardClick = (cardId) => {
		setActiveId(cardId);
		if (!matches) {
			setOpen(true);
		}
	};

	const handleGroupCardClick = (cardId) => {
		setGroupActiveId(cardId);
		if (!matches) {
			setOpen(true);
		}
	};

	const handleTogglePosts = () => {
		setTogglePosts(!togglePosts);
	};

	const handleClose = () => {
		setOpen(false);
	};
	// Posts loaded
	if (listings) {
		// Case 1: All Posts
		if (!togglePosts) {
			// Subcase 1: No Posts
			if (!Object.keys(listings).length) {
				return <NoPosts />;
			}
			// Subcase 2: Default
			else {
				return (
					<Box sx={{ mx: { xs: "0px", lg: "100px" } }}>
						<Stack>
							<Box
								sx={{
									display: "flex",
									gap: "100px",
									justifyContent: "space-between",
									alignItems: "center",
									mx: { xs: "10px", lg: "0px" },
								}}>
								<Typography variant='h5' fontWeight={"medium"}>
									Latest Opportunities
								</Typography>
								{/* <SearchBar sx={{ display: { xs: "none", md: "block" } }} /> 
			<IconButton sx={{ display: { xs: "block", md: "none" } }}>
				<SearchIcon sx={{ color: "primary.main" }} />
			</IconButton>*/}
							</Box>
							{/* Toggle Posts */}
							<Stack
								direction='row'
								spacing={1}
								alignItems='center'
								sx={{
									display: userExec ? "flex" : "none",
								}}>
								<Typography
									sx={{
										fontWeight: togglePosts ? "regular" : "bold",
									}}>
									All Posts
								</Typography>
								<Switch onClick={handleTogglePosts} />
								<Typography
									sx={{
										fontWeight: togglePosts ? "bold" : "regular",
									}}>
									My Posts
								</Typography>
							</Stack>
						</Stack>
						<Stack direction={"row"} sx={{ mb: 2 }}>
							<ListingsBox
								clickFunction={handleCardClick}
								activeId={activeId}
								posts={listings}
							/>
							<ExpandedCard listing={listings[activeId]} />
							<MobileCard
								listing={listings[activeId]}
								handleClose={handleClose}
								open={open}
							/>
						</Stack>
						<FeedbackForm />
					</Box>
				);
			}
		}
		// Case 2: Group Posts
		else {
			// Subcase 1: No Posts
			if (!Object.keys(groupListings).length) {
				return (
					<Box sx={{ mx: { xs: "0px", lg: "100px" } }}>
						<Stack>
							<Box
								sx={{
									display: "flex",
									gap: "100px",
									justifyContent: "space-between",
									alignItems: "center",
									mx: { xs: "10px", lg: "0px" },
								}}>
								<Typography variant='h5' fontWeight={"medium"}>
									Latest Opportunities
								</Typography>
								{/* <SearchBar sx={{ display: { xs: "none", md: "block" } }} /> 
							<IconButton sx={{ display: { xs: "block", md: "none" } }}>
								<SearchIcon sx={{ color: "primary.main" }} />
							</IconButton>*/}
							</Box>
							{/* Toggle Posts */}
							<Stack direction='row' spacing={1} alignItems='center'>
								<Typography
									sx={{
										fontWeight: togglePosts ? "regular" : "bold",
									}}>
									All Posts
								</Typography>
								<Switch onClick={handleTogglePosts} />
								<Typography
									sx={{
										fontWeight: togglePosts ? "bold" : "regular",
									}}>
									My Posts
								</Typography>
							</Stack>
						</Stack>
						<NoSavedPosts />
						<FeedbackForm />
					</Box>
				);
			}
			// Subcase 2: Default
			else {
				return (
					<Box sx={{ mx: { xs: "0px", lg: "100px" } }}>
						<Stack>
							<Box
								sx={{
									display: "flex",
									gap: "100px",
									justifyContent: "space-between",
									alignItems: "center",
									mx: { xs: "10px", lg: "0px" },
								}}>
								<Typography variant='h5' fontWeight={"medium"}>
									Latest Opportunities
								</Typography>
								{/* <SearchBar sx={{ display: { xs: "none", md: "block" } }} /> 
							<IconButton sx={{ display: { xs: "block", md: "none" } }}>
								<SearchIcon sx={{ color: "primary.main" }} />
							</IconButton>*/}
							</Box>
							{/* Toggle Posts */}
							<Stack
								direction='row'
								spacing={1}
								alignItems='center'
								sx={{
									display: userExec ? "flex" : "none",
								}}>
								<Typography
									sx={{
										fontWeight: togglePosts ? "regular" : "bold",
									}}>
									All Posts
								</Typography>
								<Switch onClick={handleTogglePosts} />
								<Typography
									sx={{
										fontWeight: togglePosts ? "bold" : "regular",
									}}>
									My Posts
								</Typography>
							</Stack>
						</Stack>
						<Stack direction={"row"} sx={{ mb: 2 }}>
							<ListingsBox
								clickFunction={handleGroupCardClick}
								activeId={groupActiveId}
								posts={groupListings}
							/>
							<ExpandedCard listing={groupListings[groupActiveId]} />
							<MobileCard
								listing={groupListings[groupActiveId]}
								handleClose={handleClose}
								open={open}
							/>
						</Stack>
						<FeedbackForm sx={{ mt: 2 }} />
					</Box>
				);
			}
		}
	}
	// Posts not loaded
	else {
		return <Loading></Loading>;
	}
};

export default ViewPosts;
