import React from "react";
import { useState, useEffect } from "react";
import { styled } from "@mui/material/styles";
import "./Main.css";

import GitHubIcon from "./GitHub.png";
import LinkedInIcon from "./LinkedInLogo.png";
import HomeLogo from "./Logo.png";

const WORK_DURATION = 25 * 60;
const SHORT_BREAK_DURATION = 5 * 60;
const LONG_BREAK_DURATION = 10 * 60;

const ImgNoMargin = styled("img")({
	maxWidth: "100%",
	maxHeight: "100%",
});

const Planner = () => {
	const [timer, setTimer] = useState(WORK_DURATION);
  	const [timerRunning, setTimerRunning] = useState(false);
	const [selectedTimerOption, setSelectedTimerOption] = useState('work');

	const updateTimer = () => {
		switch (selectedTimerOption) {
			case 'work':
				setTimer(WORK_DURATION);
				break;
			case 'shortBreak':
				setTimer(SHORT_BREAK_DURATION);
				break;
			case 'longBreak':
				setTimer(LONG_BREAK_DURATION);
				break;
			default:
				break;
		}
	};

	useEffect(() => {
		updateTimer();
	}, [selectedTimerOption]);

	const handleTick = () => {
		if (timer > 0) {
			setTimer((prevTimer) => prevTimer - 1);
		} else {
			setTimerRunning(false);
		}
	};

	useEffect(() => {
		let timerInterval;

		if (timerRunning) {
			timerInterval = setInterval(handleTick, 1000);
		}

		return () => clearInterval(timerInterval);
	}, [timerRunning, timer]);

	const handleTimerToggle = () => {
		setTimerRunning((prevRunning) => !prevRunning);
	};

	const handleTimerOptionChange = (newOption) => {
		setTimerRunning(false);
		setSelectedTimerOption(newOption);
	};

	const formatTime = (seconds) => {
		const minutes = Math.floor(seconds / 60);
		const remainingSeconds = seconds % 60;
		return `${String(minutes).padStart(2, '0')}\u2009:\u2009${String(remainingSeconds).padStart(2, '0')}`;
	};

	const [mood, setMood] = useState(null);

	const handleMoodSelection = (selectedMood) => {
		setMood(selectedMood);
	};

	const renderMoodButtons = () => {
		const moodOptions = [1, 2, 3, 4, 5];

		return (
		  <div style={{ width: "100%" }}>
			{moodOptions.map((option) => (
			  <button
				key={option}
				className={`mood-button ${mood === option ? "selected" : ""}`}
				onClick={() => handleMoodSelection(option)}
			  >
				{option}
			  </button>
			))}
		  </div>
		);
	};

	const [sleepHours, setSleepHours] = useState(7);

	const [highPriorityTasks, setHighPriorityTasks] = useState([
		{ id: 1, label: 'tell james he is stupid', checked: false },
		{ id: 2, label: 'say hello', checked: false },
		{ id: 3, label: 'pet a bear', checked: false },
		{ id: 4, label: 'bear a pet', checked: false },
	]);
	const [lowPriorityTasks, setLowPriorityTasks] = useState([
		{ id: 1, label: 'what is what', checked: false },
		{ id: 2, label: 'who is who', checked: false },
		{ id: 3, label: 'what is this', checked: false },
		{ id: 4, label: 'this is what', checked: false },
	]);
	const [contactList, setContactList] = useState([
		{ id: 1, label: 'call mom', checked: false },
		{ id: 2, label: 'call dad', checked: false },
		{ id: 3, label: 'text zach', checked: false }
	]);
	const [dailyChecklist, setDailyChecklist] = useState([
		{ id: 1, label: 'workout', checked: false },
		{ id: 2, label: 'run', checked: false },
		{ id: 3, label: 'duolingo', checked: false },
		{ id: 4, label: 'meditate (5 minutes)', checked: false },
		{ id: 5, label: 'stretch (5 minutes)', checked: false },
		{ id: 6, label: 'read (30 minutes)', checked: false },
		{ id: 7, label: 'concept studying (30 minutes)', checked: false },
		{ id: 8, label: 'journal', checked: false },
		{ id: 9, label: 'walk 9,000 steps', checked: false },
	]);

	const ChecklistItem = ({ label, checked, onToggle }) => {
		return (
			<div className="checklist-item">
				<input type="checkbox" checked={checked} onChange={onToggle} />
				<label>{label}</label>
			</div>
		);
	};

	const handleToggle = (list, setList, itemId) => {
		setList((prevList) =>
			prevList.map((item) =>
			item.id === itemId ? { ...item, checked: !item.checked } : item
			)
		);
	};

    return (
		<div className="view">
			<div 
				style={{
					backgroundColor: "#f0fff1",
					minWidth: "calc(100vw - 40px)",
					minHeight: "calc(100vh - 40px)",
					position: "relative",
					overflow: "hidden"
				}}
			>
				<div
					style={{
						zIndex: 2,
						display: "flex",
						justifyContent: "space-between",
						alignItems: "center",
						width: "calc(100vw - 160px)",
						backgroundColor: "#f0fff1",
						padding: "15px 60px 15px",
						position: "fixed"
					}}
				>
					<a href="/">
						<img src={HomeLogo} width={"60px"} alt='Logo' />
					</a>
					<div
						style={{
							display: "flex",
							alignItems: "center"
						}}
					>	
						<a href="https://github.com/mkim45" target="_blank" rel="noopener noreferrer" underline="none">
							<ImgNoMargin
								alt="githubtop"
								src={GitHubIcon}
								sx={{ height: "40px", width: "40px", paddingRight: "20px" }}
							/>
						</a>
						<a href="https://www.linkedin.com/in/michael-kim-nu/" target="_blank" rel="noopener noreferrer" underline="none">
							<ImgNoMargin
								alt="linkedintop"
								src={LinkedInIcon}
								sx={{ height: "40px", width: "40px" }}
							/>
						</a>
					</div>
				</div>
				<div 
					style={{ 
						marginLeft: "40px",
						marginRight: "40px"
					}}
				>
					<div className="planner-container">
						<div
							style={{
								display: "flex",
								flexDirection: "row",
								justifyContent: "space-between",
								width: "100%"
							}}
						>
							<div 
								className="checklist-container"
								style={{
									width: "18%"
								}}
							>
								<h2 style={{ margin: "20px 0px 20px 0px" }}>high priority tasks</h2>
