import React from "react";
import { Stack, Box } from "@mui/material";
import LeftSection from "./LeftSection";
import RightSection from "./RightSection";
import Hero from "./Hero";
import SignUp from "./SignUp";
import Footer from "../Footer";
import OpportunitiesImage from "../Landing/viewposts.png";
import RelationshipImage from "../Landing/discussionpost.png";
import Video from "../Landing/cardiacrehab.mp4"
import SummariesImage from "../Landing/message.png";
import Banner from "./Banner";

const ListingCard = ({ listing, isActive, clickFunction }) => {
	return (
		<Paper
			elevation={3}
			sx={{
				mt: 1,
				mx: 2,
				border: 2,
				borderColor: isActive ? "primary.main" : "transparent",
				borderRadius: "30px",
				minWidth: { xs: "100%", sm: "100%", md: "400px", lg: "515px" },
				maxWidth: "515px",
			}}>
			<StyledButtonBase
				sx={{
					justifyContent: "left",
					width: "100%",
					borderRadius: "30px",
					px: "35px",
					py: "23px",
					"&.Mui-active": {
						border: "4px solid #482FD7",
					},
				}}
				onClick={() => clickFunction(listing.id)}>
				<Stack gap={2} width='100%' direction="horizontal" justifyContent='left' whiteSpace='nowrap'>
					<Grid item>
						<Img
							alt={listing.company}
							src={listing.image}
							sx={{ width: 108, height: 108, borderRadius: "5px" }}
						/>
					</Grid>
					<Grid  item xs sm container width="10%" >
						<Grid
							item
							xs
							container
							spacing={2}
							>
							<Grid item xs>
								<Typography
									textAlign={"left"}
									gutterBottom
									variant='h6'
									whiteSpace='nowrap'
									fontWeight={"bold"}
									textOverflow='ellipsis'
									overflow='hidden'
									component='div'>
									{listing.company_name}
								</Typography>
								<Typography variant='body2' gutterBottom 
									textAlign={"left"} 
									textOverflow='ellipsis' 
									whiteSpace='nowrap'
									overflow='hidden'>
									{`${listing.description}`}

								</Typography>
							</Grid>
						</Grid>
					</Grid>
				</Stack>
			</StyledButtonBase>
		</Paper>
	);
};

const Landing = () => {
	return (
		<Stack
			spacing={{ xs: 1, sm: 3, md: 10 }}
			sx={{
				overflow: "hidden",

			}}>
			{/* <Hero></Hero>
			<Stack sx = {{alignItems: 'center', just: "center", width: '100%'}}>
				<Box width={{ xs: 400, sm: 600, md: 1200 }}
				sx = {{
						justifyContent: 'center',
						alignItems: 'center',
						display: "flex",

				}}>
					{
					<video controls width='100%'>
						<source src={Video} type="video/mp4" />
						Your browser does not support the video tag.
					</video>
					}
				</Box>
			</Stack> */}
			{/* <Stack spacing={{ xs: 1, sm: 3, md: 10 }} sx={{}}>
				<LeftSection
					mainText={[
						"View",
						"professional opportunities",
						"posted directly to",
						"your groups",
						"by employers and student leaders",
					]}
					bodyText='Gain access to full-time roles, internships, corporate events, and announcements'
					image={OpportunitiesImage}></LeftSection>
				{
				<RightSection
					mainText={[
						"Develop",
						"individual relationships",
						"with recruiters through comments",
					]}
					bodyText='Post comments on opportunities shared by recruiters to ask questions'
					image={RelationshipImage}></RightSection>
				<LeftSection
					mainText={["Receive", "weekly summaries", "directly into your inbox"]}
					bodyText='Customize the frequency and types of opportunities that are sent your way'
					image={SummariesImage}></LeftSection>
				}
			</Stack> */}
			{/*<SignUp></SignUp>*/}
			{/* <Footer></Footer> */}
		</Stack>
	);
};

export default Landing;
