import { Search, KeyboardBackspace } from "@mui/icons-material";
import {
	Box,
	Button,
	InputAdornment,
	TextField,
	Typography,
} from "@mui/material";
import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import {
	getClubs,
	getPost,
	modifyGroupOnPost,
	getUserExecClubs,
} from "../../utilities/firebase";
import GroupPaper from "./GroupPaper";
import { useAuth } from "../../contexts/AuthContext";
import NotExec from "./NotExec";
import NoGroupsFound from "./NoGroupsFound";

const ClubSelect = () => {
	const [search, setSearch] = useState("");
	const [groups, setGroups] = useState([]);
	const [selected, setSelected] = useState(null);
	const [loading, setLoading] = useState(true);
	const location = useLocation();
	const navigate = useNavigate();
	const postId = location.state.postId;
	const { currentUser } = useAuth();

	useEffect(() => {
		const fetchData = async () => {
			setLoading(true);
			const postData = await getPost(postId);
			const userClubs = await getUserExecClubs(currentUser["user_id"]);
			if (postData.posted_to) {
			}
			setGroups(userClubs);
			setLoading(false);
		};

		fetchData().catch(console.error);
	}, []);

	const _handleSearchChange = (event) => {
		setSearch(event.target.value);
	};

	const handleSetSelected = (id, isAdd) => {
		if (isAdd) {
			setSelected(id);
		} else {
			setSelected(null);
		}
	};

	const handleBack = () => {
		navigate("/postOpportunity/details", {
			state: { postId: postId },
		});
	};

	const handleSubmit = async () => {
		let selectedGroup = { school_id: "northwestern", group_id: selected };
		await modifyGroupOnPost(postId, selectedGroup);
		navigate("/posts");
	};

	if (groups.length == 0 && !loading) {
		return (
			<Box
				marginX={"100px"}
				marginY={"2rem"}
				display='flex'
				flexDirection='column'
				gap={"25px"}>
				<Box display='flex' justifyContent={"space-between"} alignItems='center'>
					<Box
						onClick={handleBack}
						display='flex'
						gap={"14px"}
						alignItems={"center"}>
						<KeyboardBackspace />
						<Typography>Back</Typography>
					</Box>
					<Button onClick={handleSubmit} variant='contained' disabled={selected ? false : true}>
						Submit
					</Button>
				</Box>
				<Typography variant='h4' fontWeight={500}>
					Select Group
				</Typography>
				<Box>
					<TextField
						id='input-with-icon-textfield'
						fullWidth
						value={search}
						onChange={_handleSearchChange}
						placeholder='Search for groups...'
						InputProps={{
							startAdornment: (
								<InputAdornment position='start'>
									<Search />
								</InputAdornment>
							),
						}}
						variant='standard'
					/>
				</Box>
				<NotExec />
			</Box>
		);
	}

	return (
		<Box
			marginX={"100px"}
			marginY={"2rem"}
			display='flex'
			flexDirection='column'
			gap={"25px"}>
