import React from "react";
import { useEffect, useState } from 'react';
import { Stack, Box } from "@mui/material";
import LeftSection from "./LeftSection";
import RightSection from "./RightSection";
import Hero from "./Hero";
import SignUp from "./SignUp";
import Footer from "../Footer";
import OpportunitiesImage from "../Landing/viewposts.png";
import RelationshipImage from "../Landing/discussionpost.png";
import Video from "../Landing/cardiacrehab.mp4"
import SummariesImage from "../Landing/message.png";
import Banner from "./Banner";
import Image from "../Landing/Auditboard.png"
import { styled } from "@mui/material/styles";
import ButtonBase from "@mui/material/ButtonBase";
import Typography from "@mui/material/Typography";
import { Grid, Paper } from "@mui/material";
import TextField from '@mui/material/TextField';
import SearchBar from "./SearchBar.jsx";
import firestore from '../../Utilities/firebase';
import { doc, setDoc, collection, getDocs } from "firebase/firestore"; 

const CustomButton = ({ children, onClick }) => (
	<ButtonBase
	  onClick={onClick}
	  sx={{
		width: "100%",
		height: "100%",
		textAlign: "center",
		backgroundColor: "#414fb6",
		"&:hover": {
		  backgroundColor: "#35409e", // Add a darker shade on hover
		},
	  }}
	>
	  {children}
	</ButtonBase>
  );

const StyledButtonBase = styled(ButtonBase)(({ theme }) => ({
	"&.Mui-active": {
		border: `4px solid ${theme.palette.primary}`,
	},
}));

const Img = styled("img")({
	margin: "auto",
	display: "block",
	maxWidth: "100%",
	maxHeight: "100%",
});

const ListingCard = ({ listing, isActive }) => {
	const [savedBid, setSavedBid] = React.useState(0);
  	const [newBidValue, setNewBidValue] = React.useState(0);
	return (
	  <Paper
		elevation={3}
		sx={{
		  mt: 1.5,
		  mx: 2,
		  border: 2,
		  borderColor: isActive ? "primary.main" : "transparent",
		  borderRadius: "30px",
		  minWidth: { xs: "100%", sm: "100%", md: "400px", lg: "515px" },
		  maxWidth: "1410px",
		  height: "100px",
		}}
	  >
		<Box
		  sx={{
			justifyContent: "flex-start",
			width: "100%",
			borderRadius: "30px",
			px: "14px",
			py: "4px",
			"&.Mui-active": {
			  border: "4px solid #482FD7",
			},
		  }}
		>
		  <Box
			  flex={1}
			  sx={{
				display: "flex",
				flexDirection: "horizontal",
				justifyContent: "space-between",
				height: "100%", 
				alignItems: "center"
			  }}
			>
			<Box
			  flex={1}
			  sx={{
				display: "flex",
				flexDirection: "horizontal",
				height: "100%", 
				alignItems: "center",
				maxWidth: 988
			  }}
			>
			<Box
    			maxWidth={88}
				height={88}
   				sx={{
      				display: "flex",
      				justifyContent: "flex-start",
      				alignItems: "center",
    			}}
  			>
    			<Img
      				alt={listing.company_name}
      				src={listing.image}
      				sx={{ maxWidth: 88, maxHeight: 88, width: "auto", height: "auto", borderRadius: "5px" }}
    			/>
  			</Box>
				<Box
			  	flex={1}
			  	sx={{
					display: "flex",
					flexDirection: "column",
					justifyContent: "space-between",
					height: "100%", 
					maxWidth: 898,
					paddingLeft: 2
			  	}}
			  >
			  <Typography
				textAlign="left"
				variant="h6"
				fontWeight="bold"
				sx={{
				  overflow: "hidden",
				  textOverflow: "ellipsis",
				  whiteSpace: "nowrap",
				}}
				component="div"
			  >
				{listing.company_name}
			  </Typography>
			  <Typography
				variant="subtitle2"
				textAlign="left"
				sx={{
				  display: "-webkit-box",
				  overflow: "hidden",
				  textOverflow: "ellipsis",
				  WebkitLineClamp: 2, 
				  WebkitBoxOrient: "vertical",
				}}
			  >
				{listing.description}
			  </Typography>
			  </Box>
			  </Box>
			  <Box
			  	sx={{
					paddingRight: 2,
					paddingLeft: 2
				}}>
			  	<Typography variant="body2">
              		Saved Bid: {savedBid}
              	</Typography>
			  </Box>
			  <Box>
			  	<TextField
              		type="number"
              		label="New Bid"
              		variant="outlined"
              		value={newBidValue}
              		onChange={(event) => setNewBidValue(event.target.value)}
             	 />
			  </Box>
			</Box>
		</Box>
	  </Paper>
	);
  };

  const fileInput = document.createElement('input');
  fileInput.type = 'file';
  const resultArray = [];

fileInput.addEventListener("change", function(event) {
    const selectedFile = event.target.files[0];

    if (!selectedFile) {
        console.log("No file selected.");
        return;
    }

    // Use FileReader to read the contents of the file as text
    const reader = new FileReader();

    reader.onload = function(event) {
        const csvData = event.target.result;
        console.log("CSV Data as String:", csvData);

        const lines = csvData.split('\n');
		const headings = lines[0].split(',');
		for (let i = 1; i < lines.length; i++) {
    		const values = lines[i].split(',');
    		const obj = {};
		    for (let j = 0; j < headings.length; j++) {
  		      obj[headings[j]] = values[j];
   			}
   			resultArray.push(obj);
		}
    };

    reader.readAsText(selectedFile);
});


  async function uploadCSVDataToFirebase() {
		for (const row of resultArray) {
			row["bids"] = { "None": 0 };
			row["open"] = true;
			row["week"] = weekInput.value;
			await setDoc(doc(firestore, "companies", row["Company"]), row);
			console.log('Document created');
		}
	}

  const weekInput = document.createElement('input');
  weekInput.type = 'text';
  weekInput.placeholder = 'Enter week';

  const startDatetimeInput = document.createElement('input');
  startDatetimeInput.type = 'datetime-local';

  const endDatetimeInput = document.createElement('input');
  endDatetimeInput.type = 'datetime-local';

  // Create a button element to upload data to Firebase
  const uploadButton = document.createElement('button');
  uploadButton.textContent = 'Upload to Firebase';

  function createSection(title, elements) {
	  const section = document.createElement('div');
	  section.style.paddingLeft = '20px';
	  section.style.paddingRight = '20px';

	  const titleLabel = document.createElement('h2');
	  titleLabel.textContent = title;
	  section.appendChild(titleLabel);

	  elements.forEach(element => {
		  section.appendChild(element);
		  section.appendChild(document.createElement('br'));
	  });

	  section.appendChild(document.createElement('hr'));

	  return section;
  }

const post1 = {
		company_name: "LiveFlow",
		image: "https://assets-global.website-files.com/61f27b4a37d6d702688002ba/61f27b4a37d6d7680b800301_LiveFlow%2520Logo%2520Deep%2520Blue%2520%255Bupdated%255D-p-500.png",
		description: "Embedded email app that optimizes email marketing.",
		// other properties...
}
const post2 = {
	company_name: "Verta",
	image: "https://mma.prnewswire.com/media/1835089/Verta_Logo.jpg?p=facebook",
	description: "Inventor of the virtual try-on for glasses with 4.8M monthly users.",
	// other properties...
}
const post3 = {
	company_name: "Torc",
	image: "https://mms.businesswire.com/media/20220301005074/en/1373252/5/Torc_logo_%28black_Torc%29.jpg",
	description: "Automated data transformation platform",
	// other properties...
}
const post4 = {
	company_name: "Yellowbird Holdings",
	image: "https://goyellowbird.com/wp-content/uploads/2022/11/logo.png",
	description: "ePerscription offers electronic perscription transmission solutions to healthcare providers and their customers.",
	// other properties...
}
const post5 = {
	company_name: "Venteur",
	image: "https://uploads-ssl.webflow.com/6189aac5f6eda92553cd1442/6189b1a6d5e56cade3903e10_ventuer-logo.svg",
	description: "Synthesized data generation workflow platform-as-a-service for AI/ML use cases.",
	// other properties...
}
const post6 = {
	company_name: "Amenities Health",
	image: "https://images.squarespace-cdn.com/content/v1/63fd6936162b8349a0b45729/b233769b-cfe0-4d2e-8d62-1ec582d1d5b9/main-logo.png",
	description: "AI-powered API documentation generator eliminating human error and creating scalable value for API companies.",
	// other properties...
}

const current_date = "07/31/2023"
const user = true;

// Append input elements and button to the document body
if (!user) {
	document.body.appendChild(createSection('Upload CSV Data', [fileInput, weekInput, startDatetimeInput, endDatetimeInput, uploadButton]));
}

const ListingCardContainer = ({ companiesCollection, current_date }) => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchAndProcessData = async () => {
      try {
        const querySnapshot = await getDocs(companiesCollection);
        const filteredPosts = [];

        querySnapshot.forEach((doc) => {
          const data = doc.data();
          const week = data["week"];

          if (week === current_date) {
            filteredPosts.push({
              company_name: data.Company,
              image: data["Image Link"],
              description: data["One-Liner"],
            });
          }
        });

        setPosts(filteredPosts);
      } catch (error) {
        console.error('Error retrieving data:', error);
      }
    };

    fetchAndProcessData();
  }, [companiesCollection, current_date]);

  return (
