 import { Box, Stack, Typography, IconButton, Switch } from "@mui/material";
import React from "react";
import useMediaQuery from "@mui/material/useMediaQuery";
import { useState, useEffect } from "react";
import LeftSection from "./LeftSection";
import RightSection from "./RightSection";
import Hero from "./Hero";
import SignUp from "./SignUp";
import Footer from "../Footer";
import OpportunitiesImage from "../Landing/viewposts.png";
import RelationshipImage from "../Landing/discussionpost.png";
import Video from "../Landing/cardiacrehab.mp4"
import SummariesImage from "../Landing/message.png";
import Banner from "./Banner";
import Image from "../Landing/Auditboard.png"
import { styled } from "@mui/material/styles";
import ButtonBase from "@mui/material/ButtonBase";
import { Grid, Paper } from "@mui/material";
import TextField from '@mui/material/TextField';
import SearchBar from "./SearchBar.jsx";
import firestore from '../../Utilities/firebase';
import { doc, setDoc, collection, getDocs } from "firebase/firestore"; 
import ListingCard from "./ListingCard";

import ExpandedCard from "./ExpandedCard";
import ListingsBox from "./ListingsBox";
import SearchIcon from "@mui/icons-material/Search";

import { navigateTo } from "../../Utilities/navigate";
import { useNavigate } from "react-router-dom";

const CustomButton = ({ children, onClick }) => (
	<ButtonBase
	  onClick={onClick}
	  sx={{
		width: "100%",
		height: "100%",
		textAlign: "center",
		backgroundColor: "#414fb6",
		"&:hover": {
		  backgroundColor: "#35409e", // Add a darker shade on hover
		},
	  }}
	>
	  {children}
	</ButtonBase>
  );

  const fileInput = document.createElement('input');
  fileInput.type = 'file';
  const resultArray = [];

fileInput.addEventListener("change", function(event) {
    const selectedFile = event.target.files[0];

    if (!selectedFile) {
        console.log("No file selected.");
        return;
    }

    // Use FileReader to read the contents of the file as text
    const reader = new FileReader();

    reader.onload = function(event) {
        const csvData = event.target.result;
        console.log("CSV Data as String:", csvData);

        const lines = csvData.split('\n');
		const headings = lines[0].split(',');
		for (let i = 1; i < lines.length; i++) {
    		const values = lines[i].split(',');
    		const obj = {};
		    for (let j = 0; j < headings.length; j++) {
  		      obj[headings[j]] = values[j];
   			}
   			resultArray.push(obj);
		}
    };

    reader.readAsText(selectedFile);
});


  async function uploadCSVDataToFirebase() {
		for (const row of resultArray) {
			row["bids"] = { "None": 0 };
			row["open"] = true;
			row["week"] = weekInput.value;
			if (row["Battery Fit"] === "Low product fit with battery's interests") {
				row["Battery Fit"] = "3";
			  } else if (row["Battery Fit"] === "Medium product fit with battery's interests") {
				row["Battery Fit"] = "2";
			  } else if (row["Battery Fit"] === "Strong product fit with battery's interests") {
				row["Battery Fit"] = "1";
			  }
			await setDoc(doc(firestore, "companies", row["Company"]), row);
			console.log('Document created');
		}
	}

  const weekInput = document.createElement('input');
  weekInput.type = 'text';
  weekInput.placeholder = 'Enter week';

  const startDatetimeInput = document.createElement('input');
  startDatetimeInput.type = 'datetime-local';

  const endDatetimeInput = document.createElement('input');
  endDatetimeInput.type = 'datetime-local';

  // Create a button element to upload data to Firebase
  const uploadButton = document.createElement('button');
  uploadButton.textContent = 'Upload to Firebase';

  function createSection(title, elements) {
	  const section = document.createElement('div');
	  section.style.paddingLeft = '20px';
	  section.style.paddingRight = '20px';

	  const titleLabel = document.createElement('h2');
	  titleLabel.textContent = title;
	  section.appendChild(titleLabel);

	  elements.forEach(element => {
		  section.appendChild(element);
		  section.appendChild(document.createElement('br'));
	  });

	  section.appendChild(document.createElement('hr'));

	  return section;
  }

const date = "07/31/2023"
const user = true;

// Append input elements and button to the document body
if (!user) {
	document.body.appendChild(createSection('Upload CSV Data', [fileInput, weekInput, startDatetimeInput, endDatetimeInput, uploadButton]));
}

function sortByFit(posts) {
	var fitArr = [];
	var objectHash = {};
	for (const post in posts) {
		const fitCompany = posts[post]["Battery Fit"] + posts[post]["Company"];
		objectHash[fitCompany] = post;
		fitArr.push(fitCompany);
	}
	fitArr.sort((a, b) => a.localeCompare(b));
	const finalMap = {};
	for (let i = 0; i < fitArr.length; i++) {
		finalMap[objectHash[fitArr[i]]] = posts[objectHash[fitArr[i]]];
	}
	return finalMap;
}

const ViewPosts = () => {
	const [listings, setListings] = useState(null);
	const [activeId, setActiveId] = useState(null);
	const [togglePosts, setTogglePosts] = useState(false);
	const [groupListings, setGroupListings] = useState(null);
	const [groupActiveId, setGroupActiveId] = useState(null);
	const [userExec, setUserExec] = useState(false);
	const [open, setOpen] = React.useState(false);
	const today = new Date();
	const navigate = useNavigate();


	// add date query
	// change if admin vs. user
	useEffect(() => {
		const fetchData = async () => {
			// if (!currentUser) {
			// 	navigate("/signin");
			// }
			let posts = {};
			let companies = collection(companies);
			for (let company of companies) {
				const companyID = company.id;
				let open = company.open;
				let companyDate = company.week;
				if (!open) {
					continue;
				}
				if (companyDate === date) {
					posts[companyID] = company;
				}
			}
			posts = sortByFit(posts);
			setActiveId(Object.keys(posts)[0]);
			setListings(posts);
		};

		fetchData().catch(console.error);
	}, []);

	const handleCardClick = (cardId) => {
		setActiveId(cardId);
		setOpen(true);
	};

	// const handleTogglePosts = () => {
	// 	setTogglePosts(!togglePosts);
	// };

	const handleClose = () => {
		setOpen(false);
	};

	// Posts loaded
	if (listings) {
		const handleClick = () => {
			// Define the action you want to perform when the button is clicked
			console.log("Button clicked!");
		  };
		if (user)
		{
		return (
			<Stack
				spacing={{ xs: 1, sm: 3, md: 3 }}>
					<Box
						flex={1}
						sx={{
						  display: "flex",
						  flexDirection: "horizontal",
						  justifyContent: "space-between",
						  height: "100%", 
						  alignItems: "center",
						  mx: 6,
						  mt: 2
						}}
					>
						<Box>
							<Box
								sx={{ 
									display: "flex",
									flexDirection: "column",
									justifyContent: "space-between",
									alignItems: "center",
									height: "100%", 
									mx: 2
							}}>
								<Typography 
									variant="h5"
									fontFamily="Poppins"
									color="#414fb6"
									sx={{
										overflow: "hidden",
										textOverflow: "ellipsis",
										  whiteSpace: "nowrap",
										fontWeight: 700,
									}}
								component="div">
									  Extern Sourced Companies - {date}
								  </Typography>
								<Box 
									sx={{ 
										display: "flex",
										flexDirection: "horizontal",
										height: "100%", 
										justifyContent: "center",
										alignItems: "center",
								}}>
									<Paper
										elevation={3}
										sx={{
											  mt: 3,
											  mx: 2,
											  border: 2,
											  borderColor: "transparent",
											  borderRadius: "10px",
											  minWidth: { xs: "100%", sm: "100%", md: "200px", lg: "250px" },
											  maxWidth: "250px",
											  height: "40px",
											backgroundColor: "#414fb6",
										}}
									  >
										<Box
											 sx={{
												justifyContent: "flex-start",
												width: "100%",
												borderRadius: "10px",
												px: "14px",
												py: "4px",
												"&.Mui-active": {
													  border: "4px solid #482FD7",
												},
												textAlign: "center",
												color: "#fff"
											  }}
										>
											<CustomButton onClick={handleClick}>
											<Typography sx={{ fontWeight: 600 }}>
												Previous Week
											</Typography>
											</CustomButton>
										</Box>
									</Paper>
									<Paper
										elevation={3}
										sx={{
											  mt: 3,
											  mx: 2,
											  border: 2,
											  borderColor: "transparent",
											  borderRadius: "10px",
											  minWidth: { xs: "100%", sm: "100%", md: "200px", lg: "250px" },
											  maxWidth: "250px",
											  height: "40px",
											backgroundColor: "#414fb6"
										}}
									  >
										<Box
											 sx={{
												justifyContent: "flex-start",
												width: "100%",
												borderRadius: "10px",
												px: "14px",
												py: "4px",
												"&.Mui-active": {
													  border: "4px solid #482FD7",
												},
												textAlign: "center",
												color: "#fff",
											  }}
										>
											<CustomButton onClick={handleClick}>
											<Typography sx={{ fontWeight: 600 }}>
												Next Week
											</Typography>
											</CustomButton>
										</Box>
									</Paper>	
								</Box>
							</Box>
						</Box>
						<Paper
							elevation={3}
							sx={{
								  border: 2,
								  borderColor: "transparent",
								  borderRadius: "10px",
								  minWidth: { xs: "100%", sm: "100%", md: "400px", lg: "650px" },
								  maxWidth: "650px",
								  height: "120px",
							}}
						  >
							<Box
								 sx={{
									display: "flex",
									flexDirection: "column",
									justifyContent: "space-between",
									height: "100%",
									width: "100%",
									borderRadius: "10px",
									px: "14px",
									py: "10px",
									"&.Mui-active": {
										  border: "4px solid #482FD7",
									},
								  }}
							>
								<Typography variant="body2">
									<span style={{ color: "#212121", fontWeight: "bold" }}> Time Remaining: </span> <span style={{ color: "red" }}> 10hr, 10min, 13sec </span>
								  </Typography>
								<Typography variant="body2">
									<span style={{ color: "#212121", fontWeight: "bold" }}> Points Balance: </span> <span style={{ color: "#212121" }}> 100 </span>
								  </Typography>
								<Typography variant="body2">
									<span style={{ color: "#212121", fontWeight: "bold" }}> Current Week Usage: </span> <span style={{ color: "#212121" }}> 0 </span>	
								  </Typography>
							</Box>
						</Paper>
					</Box>
					<SearchBar></SearchBar>
					<ListingsBox
								clickFunction={handleCardClick}
								activeId={activeId}
								posts={listings}
							/>
					{/* <ExpandedCard listing={listings[activeId]} /> */}
					{/* {renderPosts(posts)} */}
					<Box 
									sx={{ 
										display: "flex",
										flexDirection: "horizontal",
										height: "100%", 
										justifyContent: "center",
										alignItems: "center",
								}}>
									<Paper
										elevation={3}
										sx={{
											  mx: 2,
											  border: 2,
											  borderColor: "transparent",
											  borderRadius: "10px",
											  minWidth: { xs: "100%", sm: "100%", md: "200px", lg: "250px" },
											  maxWidth: "250px",
											  height: "40px",
											backgroundColor: "#414fb6",
										}}
									  >
										<Box
											 sx={{
												justifyContent: "flex-start",
												width: "100%",
												borderRadius: "10px",
												px: "14px",
												py: "4px",
												"&.Mui-active": {
													  border: "4px solid #482FD7",
												},
												textAlign: "center",
												color: "#fff"
											  }}
										>
											<CustomButton onClick={handleClick}>
											<Typography sx={{ fontWeight: 600 }}>
												Submit Bids
											</Typography>
											</CustomButton>
										</Box>
									</Paper>
								</Box>
			</Stack>
		);
		} else {

			  return(
				// Add event listener to the upload button
				uploadButton.addEventListener('click', () => {
					// Call a function to read CSV file and upload data to Firebase
					uploadCSVDataToFirebase();
				})
			);
		}
	}
};

export default ViewPosts;
