import styled from "@emotion/styled";
import { Stack, Typography } from "@mui/material";
import React from "react";

const Img = styled("img")({
	margin: "auto",
	display: "block",
	maxHeight: "100%",
});
const LoadingPostsCard = ({ src }) => {
	return (
		<Stack
			sx={{
				p: "40px",
				m: "10px",
				borderRadius: "40px",
				textAlign: "center",
				gap: 6,
			}}>
			<Img src={src}></Img>
			<Typography variant='h5' fontWeight={"medium"}>
				Loading Posts ...
			</Typography>
		</Stack>
	);
};

export default LoadingPostsCard;
