import React, { useState } from "react";
import { Routes, Route, Navigate } from "react-router-dom";

import DefaultLayout from "./Components/DefaultLayout";
import NoScrollLayout from "./Components/NoScrollLayout";
import ViewPosts from "./Components/Landing/ViewPosts";
import SignInPage from "./Components/Landing/SignInPage"; // Import your SignInPage component

import { useMediaQuery } from "@mui/material";

import TempMobile from "./Components/TempMobile";

function App() {
  const mobile = useMediaQuery("(min-width:900px)");
  const [signedIn, setSignedIn] = useState(false); // State to track if user is signed in

  if (!mobile) {
    return <TempMobile />;
  }

  return (
    <div className="App">
      <Routes>
        {/* Set up the default sign-in route */}
        <Route
          path="/"
          element={
            <DefaultLayout>
              <Route index element={<SignInPage setSignedIn={setSignedIn} />} />
            </DefaultLayout>
          }
        >
          {/* Only show the ViewPosts component if signed in */}
          {signedIn && <Route index element={<ViewPosts user = {signedIn}/>} />}
          {/* You can add more routes for other landing pages */}
        </Route>
      </Routes>
    </div>
  );
}

export default App;
